import React from "react";
import {
  FormControl,
  // Menu,
  InputAddon,
  // MenuButton,
  // FormLabel,
  Select,
  // MenuItem,
  // MenuList,
  InputGroup,
  InputLeftAddon,
  Flex,
  Box,
} from "@chakra-ui/react";

const FilterComponent = () => {
  return (
    <>
      <Flex>
        <SortComponent />
      </Flex>
    </>
  );
};

export default FilterComponent;

const SortComponent = () => {
  return (
    <>
      <FormControl>
        <Box d="flex">
          <InputLeftAddon
            p=".5rem"
            fontSize=".85rem"
            borderTopLeftRadius=".25rem"
            borderBottomLeftRadius=".25rem"
            bg="#F1F4F3"
            overflow="hidden"
            color="gray"
            children="Sort by :"
          />

          <FormControl
            bg="#F1F4F3"
            overflow="hidden"
            borderTopRightRadius=".25rem"
            borderBottomRightRadius=".25rem"
          >
            <Select
              id="country"
              placeholder="Recommended"
              border="1px solid"
              border="none"
              pt=".4rem"
              textAlign="center"
              bg="none"
              fontWeight="semibold"
            >
              <option>Price : High to Low</option>
              <option>Price : Low to High</option>
              <option>Sort : A - Z</option>
            </Select>
          </FormControl>
          {/* </Menu> */}
        </Box>
      </FormControl>
    </>
  );
};
