import React from "react";
import { Box, Flex, Text, Button, Stack, Image } from "@chakra-ui/react";
import Bag from "../Image/Bag.png";
import User from "../Image/User.png";
import Menu from "../Image/Menu.png";
import Close from "../Image/Close.png";

function Navbar(props) {
  // eslint-disable-next-line
  const { colorMode, toggleColorMode } = useColorMode();
  const [isOpen, setIsOpen] = React.useState(false);

  const toggle = () => setIsOpen(!isOpen);

  return (
    <NavBarContainer
      {...props}
      bg={colorMode === "light" ? "bodyBackground" : ""}
    >
      <NavHashLink to="/">
        <Text as="h1" fontWeight="500" fontSize="4xl">
          Ravindra
        </Text>
      </NavHashLink>

      <MenuToggle toggle={toggle} isOpen={isOpen} />
      <MenuLinks isOpen={isOpen} />
    </NavBarContainer>
  );
}

const MenuToggle = ({ toggle, isOpen }) => {
  return (
    <Box display={{ base: "block", md: "none" }} onClick={toggle}>
      {isOpen ? <Image src={Close} alt="Dan Abramov" /> : <Image src={Close} alt="Dan Abramov" />}
    </Box>
  );
};

const MenuLinks = ({ isOpen }) => {
  return (
    <Box
      display={{ base: isOpen ? "block" : "none", md: "block" }}
      flexBasis={{ base: "100%", md: "auto" }}
    >
      <Stack
        spacing={8}
        align="center"
        justify={["center", "space-between", "flex-end", "flex-end"]}
        direction={["column", "row", "row", "row"]}
        pt={[4, 4, 0, 0]}
      >
        <a href="/">
          <Text display="block" fontWeight="500">
            Home
          </Text>
        </a>

        <a href="/#Products">
          <Text display="block" fontWeight="500">
            Products
          </Text>
        </a>

        <a href="/#Training">
          <Text display="block" fontWeight="500">
            {/* Training and Consultation */}
            Training
          </Text>
        </a>

        <a href="/#5s-Warm-Up">
          <Text display="block" fontWeight="500">
            5s Warm Up
          </Text>
        </a>
      </Stack>
    </Box>
  );
};

const NavBarContainer = ({ children, ...props }) => {
  return (
    <Flex
      as="nav"
      align="center"
      justify="space-between"
      wrap="wrap"
      w="100%"
      p={5}
      {...props}
    >
      {children}
    </Flex>
  );
};

export default Navbar;
