                  {item.title}
                </Heading>
                <Stack direction={"row"} align={"center"}>
                  <Text fontWeight={800} fontSize={"xl"}>
                    ${item.price * 2}
                  </Text>
                  <Text textDecoration={"line-through"} color={"gray.600"}>
                    ${item.price}
                  </Text>
                </Stack>
                <Box
                  pos={"relative"}
                  display="flex"
                  left={"15%"}
                  bottom={"250"}
                >
                  <Button
                    opacity="0"
                    pos={"absolute"}
                    right={"5"}
                    aria-label="left-arrow"
                    borderRadius="md"
                    // zIndex="5"
                    variant="ghost"
                    _hover={{
                      opacity: "1",
                      color: "white",
                      bg: "orange.500",
                      display: "block",
                    }}
                  >
                    <Eye size={24} />
