        })} */}
        {products.map((item, index) => {
          return (
            <Box key={index} margin="2%">
              <Image
                src={item.images}
                height={230}
                width={282}
                rounded={"md"}
                objectFit={"cover"}
                transform="scale(1.0)"
                _hover={{
                  transform: "scale(1.05)",
                  transition: "0.3s ease-in-out",
                }}
                zIndex="2"
              />
              <Stack pt={5} align={"center"}>
                <Text
                  color={"gray.500"}
                  fontSize={"sm"}
                  textTransform={"uppercase"}
                >
                  {item.category.name}
                </Text>
                <Heading fontSize={"xl"} fontFamily={"body"} fontWeight={500}>
                  {item.title}
                </Heading>
                <Stack direction={"row"} align={"center"}>
                  <Text fontWeight={800} fontSize={"xl"}>
                    ${item.price * 2}
                  </Text>
                  <Text textDecoration={"line-through"} color={"gray.600"}>
                    ${item.price}
                  </Text>
                </Stack>
                <Box
                  pos={"relative"}
                  display="flex"
                  left={"15%"}
                  bottom={"250"}
                >
                  <Button
                    opacity="0"
                    pos={"absolute"}
                    right={"5"}
                    aria-label="left-arrow"
                    borderRadius="md"
                    // zIndex="5"
                    variant="ghost"
                    _hover={{
                      opacity: "1",
                      color: "white",
                      bg: "orange.500",
                      display: "block",
                    }}
                  >
                    <Eye size={24} />
                  </Button>
                </Box>
              </Stack>
            </Box>
          );
        })}
      </Box>
    </>
  );
};

export default CartItems;
