import {
  Box,
  useColorModeValue,
  Heading,
  Text,
  Stack,
  Image,
} from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";

const IMAGE =
  "https://images.unsplash.com/photo-1518051870910-a46e30d9db16?ixlib=rb-1.2.1&ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&auto=format&fit=crop&w=1350&q=80";

function ProdContainerItem() {
  let ShowcaseProduct = [];
  // console.log(productsArray);
  const Item = HashnodeData.posts;
  if (Item.length > 0) {
    for (let index = 0; index < 8; index++) {
      ShowcaseProduct = [...ShowcaseProduct, Item[index]];
    }
  }
  return (
    <>
      <Box
        display={{ xl: "flex" }}
        flexWrap="wrap"
        justifyContent="space-between"
        mx={{ xl: "10%", lg: "10%", md: "10%" }}
        py={{ xl: "4%", lg: "4%" }}
        pt={{ sm: "1rem", md: "1rem" }}
        border="1px solid"
      >
        {ShowcaseProduct.map(function (Item, index) {
          return (
            <>
              <Box
                role={"group"}
                p={6}
                maxW={"250px"}

                // w={"full"}
                // bg={useColorModeValue("white", "gray.800")}
                // boxShadow={"2xl"}
                border="1px solid lightgrey"
                rounded={"lg"}
                // pos={"relative"}
                mb={'2'}
                zIndex={1}
              >
                <Box rounded={"md"}>
                  <Image
                    rounded={"lg"}
                    height={"100%"}
                    width={"100%"}
                    objectFit={"cover"}
                    src={Item.coverImage}
                  />
                </Box>
                <Stack pt={10} align={"center"}>
                  <Text
                    color={"gray.500"}
                    fontSize={"sm"}
                    textTransform={"uppercase"}
                  >
                    Brand
                  </Text>
                  <Heading
                    fontSize={"2xl"}
                    fontFamily={"body"}
                    fontWeight={500}
                  >
                    Nice Chair, pink
                  </Heading>
                  <Stack direction={"row"} align={"center"}>
                    <Text fontWeight={800} fontSize={"xl"}>
                      $57
                    </Text>
                    <Text textDecoration={"line-through"} color={"gray.600"}>
                      $199
                    </Text>
                  </Stack>
                </Stack>
              </Box>
            </>
          );
        })}
      </Box>
    </>
  );
}

const ProdContainerHeader = () => {
  return (
    <>
      <Box
        // px="1.5rem"
        py="1.5%"
        mx={{ base: "10%" }}
        display="flex"
        justifyContent={["center", "flex-start", "flex-start", "flex-start"]}
      >
        <Heading
          fontSize={["1.5rem", "1.75rem"]}
          fontWeight={["500", "600"]}
          as="h2"
          color="#2c2c2c"
        >
          <Box display={["inline-block"]}>Hot Products</Box>
          <Box
            borderBottom="2px solid #e97730"
            py={["1"]}
            pos={["relative"]}
            w={["70%", "60%"]}
            left={["4", "4"]}
            right={["4", "4"]}
          ></Box>
        </Heading>
      </Box>
    </>
  );
};

function TrendingProduct(props) {
  // eslint-disable-next-line

  return (
    <>
      <ProdContainer {...props}>
        <ProdContainerHeader />
        <ProdContainerItem />
      </ProdContainer>
    </>
  );
}

const ProdContainer = ({ children, ...props }) => {
  return <Box w="100vw">{children}</Box>;
};

export default TrendingProduct;
