import React from "react";
import {
  FormControl,
  Divider,
  Text,
  Flex,
  Box,
  Button,
  Tag,
  TagLabel,
  Select,
  TagLeftIcon,
  Accordion,
  AccordionItem,
  AccordionButton,
  AccordionPanel,
  AccordionIcon,
} from "@chakra-ui/react";

import { ChevronDownIcon, StarIcon } from "@chakra-ui/icons";
import { CartState } from "../Context/context";

const FilterCard = () => {
  return (
    <>
      <Flex flexDirection={"column"}>
        <SortComponent />
        <FilterComponent />
      </Flex>
    </>
  );
};

export default FilterCard;

const SortComponent = () => {
  return (
    <>
      <FormControl>
        <Box
          w={"100%"}
          display="inline-flex"
          alignItems="center"
          rounded={"md"}
          border="1px solid lightgrey"
          gap={"1rem"}
          px={"2"}
        >
          <Box>
            <Text fontWeight={"bold"} w={'50'}>Sort By :</Text>
          </Box>
          <Select border={"none"} outline={"none"} my={"2"}>
            <option>High to Low</option>
            <option>Low to High</option>
            <option>Rating</option>
          </Select>
        </Box>
      </FormControl>
    </>
  );
};

const FilterComponent = () => {
  const PriceList = [
    "Under 149",
    "Under 199",
    "Under 249",
    "Under 299",
    "Under 349",
    "Under 399",
    "Under 449",
    "Under 499",
  ];

  const {
    state: { products },
  } = CartState();
  return (
    <Flex padding=".5rem" flexDirection={"column"} gap=".5rem">
      <Box padding=".5rem">
        <Text fontWeight={"extrabold"}>FILTERS</Text>
        <Text fontSize="sm" fontWeight={"light"} color={"grey"}>
          {products.length} Products
        </Text>
      </Box>

      <Divider colorScheme={"red"} />
      <Flex flexDirection={"column"} gap=".5rem">
        {/* // as={Button}
        // bg={"white"}
        // border="1px solid lightgrey" // color={"blackAlpha.900"}
        // rightIcon={<ChevronDownIcon />}
        // > Price display={"flex"} */}
        {/* flexWrap="wrap" flexDirection="column" gap="1rem" marginRight="1rem"
        maxH="30vh" padding=".5rem" // > */}
        {/* {PriceList.map((item, index) => {
                return (
                  <MenuItem key={index}>
                    <Tag variant="outline" rounded={"full"}>
                      <TagLabel>{item}</TagLabel>
                    </Tag>
                  </MenuItem>
                );
              })} */}
        {/* <Menu>
          <MenuButton
            as={Button}
            bg={"white"}
            border="1px solid lightgrey"
            color={"blackAlpha.900"}
            rightIcon={<ChevronDownIcon />}
          >
            Rating
          </MenuButton>
          <MenuList>
            {[1, 2, 3, 4, 5].map((item, index) => {
              return (
                <MenuItem
                  display={"flex"}
                  flexWrap="wrap"
                  flexDirection="column"
                  gap="1rem"
                  marginRight="1rem"
                  maxH="30vh"
                  key={index}
                >
                  <Tag
                    variant="subtle"
                    colorScheme="blackAlpha"
                    rounded={"md"}
                    padding=".5rem"
                  >
                    <TagLeftIcon boxSize="12px" as={StarIcon} />
                    <TagLabel>{index}</TagLabel>
                  </Tag>
                </MenuItem>
              );
            })}
          </MenuList>
        </Menu> */}
        {/* Clear Out */}
        <Button display="flex" colorScheme="red">
          Clear Filters
        </Button>
      </Flex>
    </Flex>
  );
};
