import React, { useState, useEffect } from "react";
import Product from "./product.json";
import {
  Box,
  Image,
  Text,
  // useColorModeValue,
  Heading,
  Stack,
  Button,
} from "@chakra-ui/react";

import { Eye } from "@styled-icons/feather/Eye";

const CartItems = () => {
  const [Prod, setProd] = useState([]);
  const FetchPost = () => {
    let CalculatedProd = [];
    if (Product.length > 0) {
      for (let index = 0; index < 12; index++) {
        CalculatedProd = [...CalculatedProd, Product[index]];
      }
    }
    // console.log(CalculatedProd);

    setProd(CalculatedProd);
  };
  useEffect(() => {
    return () => {
      FetchPost();
    };
  }, []);

  return (
    <>
      <Box
        padding="1%"
        display="flex"
        flexWrap="wrap"
        justifyContent={"space-around"}
        alignItems="center"
      >
        {Prod.map((item, index) => {
          return (
            <Box key={index} margin="2%">
              <Stack>
              <Button
                    display="none"
                    pos={"absolute"}
                    aria-label="left-arrow"
                    left={"40%"}
                    top={"40%"}
                    transform={"translate(0%, 50%)"}
                    borderRadius="md"
                    zIndex={2}
                    variant="ghost"
                    color="#fff"
                    onClick={() => {
                      onOpen();
                      function close() {
                        toast.closeAll();
                      }
                      toast({
                        position: "bottom-left",
                        duration: "200000000",
                        isClosable: true,
                        render: () => (
                          <Modal isCentered isOpen={isOpen} onClose={onClose}>
                            <ModalOverlay
                              bg="blackAlpha.200"
                              backdropFilter="blur(.5em)"
                              fontFamily="montserrat"
                            />
                            <ModalContent>
                              <ModalHeader>
                                <Box pl="8%">
                                  <Image
                                    src={coverImage}
                                    borderRadius="md"
                                    alt={title}
                                    width={"90%"}
                                    height="auto"
                                  />
                                </Box>
                              </ModalHeader>

                              <ModalBody>
                                <Text
                                  fontSize={"2xl"}
                                  fontWeight={"bold"}
                                  fontFamily="montserrat"
                                >
                                  {title}
                                </Text>

                                <Text py="5" FontFamily="poppins">
                                  {brief}
                                </Text>
                              </ModalBody>
                              <ModalFooter>
                                <Button onClick={close}>Close</Button>
                              </ModalFooter>
                            </ModalContent>
                          </Modal>
                        ),
                      });
                    }}
                    _groupHover={{
                      color: "white",
                      bg: "orange.500",
                      display: "block",
                    }}
                  >
                    <Eye size={24} />
                  </Button>
                <Image
                  src={item.images}
                  height={230}
                  width={282}
                  objectFit={"cover"}
                />

                <Stack pt={5} align={"center"}>
                  <Text
                    color={"gray.500"}
                    fontSize={"sm"}
                    textTransform={"uppercase"}
                  >
                    {item.category.name}
                  </Text>
                  <Heading
                    fontSize={"2xl"}
                    fontFamily={"body"}
                    fontWeight={500}
                  >
                    Nice Chair, pink
                  </Heading>
                  <Stack direction={"row"} align={"center"}>
                    <Text fontWeight={800} fontSize={"xl"}>
                      ${item.price * 2}
                    </Text>
                    <Text textDecoration={"line-through"} color={"gray.600"}>
                      ${item.price}
                    </Text>
                  </Stack>
                </Stack>
              </Stack>
            </Box>
          );
        })}
      </Box>
    </>
  );
};

export default CartItems;
