// images
// import FootSole from "../Image/temp-img/FootSole.png";
// import tape from "../Image/temp-img/tape.png";
// import TShirt from "../Image/temp-img/TShirt.png";
// import YellowTape from "../Image/temp-img/YellowTape.png";

import React from "react";
import {
  Box,
  IconButton,
  useBreakpointValue,
  Stack,
  Heading,
  Grid,
  GridItem,
  Text,
  Container,
  HStack,
  Image,
} from "@chakra-ui/react";
// Here we have used react-icons package for the icons
import { BiLeftArrowAlt, BiRightArrowAlt } from "react-icons/bi";
// And react-slick as our Carousel Lib
import Slider from "react-slick";

// Settings for the slider
const settings = {
  dots: true,
  arrows: false,
  fade: true,
  infinite: true,
  autoplay: true,
  speed: 500,
  autoplaySpeed: 5000,
  slidesToShow: 1,
  slidesToScroll: 1,
};

function Herocarousel() {
  // As we have used custom buttons, we need a reference variable to
  // change the state
  const [slider, setSlider] = React.useState(<Slider />);

  // These are the breakpoints which changes the position of the
  // buttons as the screen size changes
  const top = useBreakpointValue({ base: "90%", md: "50%" });
  const side = useBreakpointValue({ base: "5%", md: "40px" });

  // This list contains all the data for carousels
  // This can be static or loaded from a server
  const cards = [
    {
      title: "Design Projects 1",
      text: "The project board is an exclusive resource for contract work. It's perfect for freelancers, agencies, and moonlighters.",
      image:
        "https://jblvfrdwrdneaiiafbov.supabase.co/storage/v1/object/sign/tempdata/TrendingProd/1.jpg?token=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1cmwiOiJ0ZW1wZGF0YS9UcmVuZGluZ1Byb2QvMS5qcGciLCJpYXQiOjE2NTkwMjYxNjMsImV4cCI6MTk3NDM4NjE2M30.aoBnngO7salw4jwu67tfukF26gDa20N0XWntqDDG-4c&t=2022-07-28T16%3A36%3A39.718Z",
    },
    {
      title: "Design Projects 2",
      text: "The project board is an exclusive resource for contract work. It's perfect for freelancers, agencies, and moonlighters.",
      image:
        "https://jblvfrdwrdneaiiafbov.supabase.co/storage/v1/object/sign/tempdata/TrendingProd/1.jpg?token=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1cmwiOiJ0ZW1wZGF0YS9UcmVuZGluZ1Byb2QvMS5qcGciLCJpYXQiOjE2NTkwMjYxNjMsImV4cCI6MTk3NDM4NjE2M30.aoBnngO7salw4jwu67tfukF26gDa20N0XWntqDDG-4c&t=2022-07-28T16%3A36%3A39.718Z",
    },
    {
      title: "Design Projects 3",
      text: "The project board is an exclusive resource for contract work. It's perfect for freelancers, agencies, and moonlighters.",
      image:
        "https://jblvfrdwrdneaiiafbov.supabase.co/storage/v1/object/sign/tempdata/TrendingProd/1.jpg?token=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1cmwiOiJ0ZW1wZGF0YS9UcmVuZGluZ1Byb2QvMS5qcGciLCJpYXQiOjE2NTkwMjYxNjMsImV4cCI6MTk3NDM4NjE2M30.aoBnngO7salw4jwu67tfukF26gDa20N0XWntqDDG-4c&t=2022-07-28T16%3A36%3A39.718Z",
    },
  ];

  return (
    <>
      <Box
        display={["null", "grid", "grid", "grid"]}
        gridTemplateColumns={[
          "null",
          ".5fr 4fr .5fr",
          ".5fr 4fr .5fr",
          ".5fr 4fr .5fr",
        ]}
      >
        <Box
          gridColumn="2/3"
          position={"relative"}
          height={["50vh", "60vh", "70vh", "80vh"]}
          width={"full"}
          overflow={"hidden"}
        >
          hello
          {/* CSS files for react-slick */}
          <link
            rel="stylesheet"
            type="text/css"
            charSet="UTF-8"
            href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick.min.css"
          />
          <link
            rel="stylesheet"
            type="text/css"
            href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick-theme.min.css"
          />
          <HSatck display="flex" justifyContent="space-around" alignItems="center">
            {/* Left Icon */}
            <IconButton
              aria-label="left-arrow"
              colorScheme="messenger"
              borderRadius="full"
              // position="absolute"
              // left={side}
              // top={top}
              // transform={"translate(0%, -50%)"}
              zIndex={2}
              onClick={() => slider?.slickPrev()}
            >
              <BiLeftArrowAlt />
            </IconButton>
            {/* Right Icon */}
            <IconButton
              aria-label="right-arrow"
              colorScheme="messenger"
              borderRadius="full"
              // position="absolute"
              // right={side}
              // top={top}
              // transform={"translate(0%, -50%)"}
              zIndex={2}
              onClick={() => slider?.slickNext()}
            >
              <BiRightArrowAlt />
            </IconButton>
          </HSatck>
        </Box>
      </Box>
    </>
  );
}

export default Herocarousel;
