import React, { useState, useEffect } from "react";
import { Grid, GridItem, Text, Box, Image } from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";

const BlogSection = () => {
  return (
    <>
      <Grid
        templateRows="1fr"
        placeItems="center"
        p="1rem"
        id="blogs&articles"
        // bg="#e9f8f3"
      >
        <BlogComponent />
      </Grid>
    </>
  );
};

const BlogComponent = () => {
  const [posts, setposts] = useState([]);

  let DisplaythreePost = [];
  // async await function

  function FetchPostData() {
    Object.entries(HashnodeData).map(function (item) {
      if (item[1].length > 0) {
        for (let index = 0; index < 3; index++) {
          DisplaythreePost = [...DisplaythreePost, item[1][index]];
        }
      }
      setposts(DisplaythreePost);
    });
  }

  useEffect(() => {
    FetchPostData();
  }, []);
  return (
    <>
      <Grid templateColumns="350px 350px 350px" columnGap="2rem">
        {posts.map(function (post, index) {
          return (
            <>
              <li key={index} style={{ listStyle: "none" }}>
                <Grid
                  templateRows="1fr"
                  placeContent="center"
                  border="1px solid #E7EBF0"
                  _hover={{
                    boxShadow:
                      "rgba(60, 64, 67, 0.3) 0px 1px 2px 0px, rgba(60, 64, 67, 0.15) 0px 2px 6px 2px;",
                    // transition:
                    transition: ".5s ease-in-out",
                  }}
                  borderRadius=".5rem"
                  bg="white"
                  key={post.id}
                >
                  <DefaultApiprops post={post} key={index} />
                </Grid>
              </li>
            </>
          );
        })}
      </Grid>
    </>
  );
};



export default BlogSection;
