import { Box, Text, Flex, Divider, Image, Button } from "@chakra-ui/react";

// context Api
import { CartState } from "./Context/context";

// icons
import RemoveIcon from "@mui/icons-material/Remove";
import AddIcon from "@mui/icons-material/Add";

const CartPageData = () => {
  return (
    <>
      <CartPageInitialComponent />
      <Box padding={"1vmax"}>
        <CartPageSecondryComponent />
      </Box>
    </>
  );
};

export default CartPageData;

const CartPageInitialComponent = () => {
  return (
    <>
      <Box
        display={"flex"}
        alignItems="center"
        justifyContent={"center"}
        padding={"1vmax"}
      >
        <Text fontSize={"5xl"} fontWeight={"bold"}>
          Cart Items
        </Text>
      </Box>
    </>
  );
};

const CartPageSecondryComponent = () => {
  return (
    <>
      <Flex w={"100vw"} gap={"2%"}>
        <CartItems />
        <Divider orientation="vertical" h="100px" />
        <CartPaymentPage />
      </Flex>
    </>
  );
};

const CartItems = () => {
  const {
    state: { cart },
    dispatch,
  } = CartState();

  return (
    <Flex
      boxShadow="inset 0 0 0 1px #e9e9eb"
      rounded={"md"}
      padding={"1vmax"}
      w={"60%"}
      flexDirection={"column"}
      gap={"1rem"}
    >
      {cart.map((item, index) => {
        console.log(item);
        return (
          <Flex
            boxShadow="inset 0 0 0 1px #e9e9eb"
            rounded={"md"}
            padding={"1vmax"}
            gap={"1rem"}
          >
            <Box key={index}>
              <Image
                src={item.image}
                alt={"image"}
                objectFit={"contain"}
                height={"auto"}
                width={"md"}
                rounded={"md"}
              />
            </Box>
            <Flex flexDirection={"column"} border="1px solid" w="100%">
              {/* title */}
              <Box>
                <Text>{item.name}</Text>
              </Box>
              {/* category */}
              <Box>
                <Text>{item.category}</Text>
              </Box>
              {/* price */}
              <Box display={"flex"} gap={"1rem"}>
                <Text> ${item.price * 2}</Text>
                <Text>${item.price}</Text>
              </Box>
              {/* buttons */}
              <Box
                display={"flex"}
                alignItems={"center"}
                justifyContent={"space-between"}
                padding={"1rem"}
                border={"1px solid"}
              >
                <Button
                  bg={"transparent"}
                  border={"1px solid red"}
                  color={"red"}
                  _hover={{
                    bg: "red",
                    color: "white",
                  }}
                >
                  <Text fontWeight={"light"}>Remove From Cart</Text>
                </Button>
                <Box display={"inline-flex"} gap={"2rem"} alignItems={"center"}>
                  <Box
                    bg={"transparent"}
                    color={"orange.500"}
                    border={"1px solid "}
                    rounded={''}
                  >
                    <AddIcon />
                  </Box>
                  <Box
                    bg={"transparent"}
                    color={"orange.500"}
                    border={"1px solid "}
                  >
                    <RemoveIcon />
                  </Box>
                </Box>
              </Box>
            </Flex>
          </Flex>
        );
      })}
    </Flex>
  );
};

const CartPaymentPage = () => {
  return (
    <>
      <Box
        w={"30vmax"}
        boxShadow="inset 0 0 0 1px #e9e9eb"
        rounded={"md"}
        padding={"1vmax"}
      >
        payment
      </Box>
    </>
  );
};
