import React from "react";
import {
  Divider,
  Text,
  Flex,
  Box,
  Button,
  Checkbox,
  CheckboxGroup,
  Radio,
  Stack,
  RadioGroup,
} from "@chakra-ui/react";

import { CartState } from "../Context/context";

const FilterCard = () => {
  return (
    <>
      <Flex flexDirection={"column"}>
        <SortComponent />
        <Divider />
        <CategoriesComponent />
        <Divider />
        <OtherComponent />
      </Flex>
    </>
  );
};

export default FilterCard;

const SortComponent = () => {
  // for sorting only
  const [value, setValue] = React.useState("");
  const {
    state: { products },
    productState: { byStock, sort },
    productDispatch,
  } = CartState();
  console.log(sort);

  const transformProducts = () => {
    let sortedProducts = products;

    if (sort) {
      sortedProducts = sortedProducts.sort((a, b) =>
        sort === "lowToHigh" ? a.price - b.price : b.price - a.price
      );
    }

    if (!byStock) {
      sortedProducts = sortedProducts.filter((prod) => prod.inStock);
    }

    return sortedProducts;
  };
  // console.log(sort, byStock);
  return (
    <>
      <Box
        my={"4"}
        display={"flex"}
        gap={"10vmax"}
        w={"100%"}
        alignItems={"center"}
        justifyContent={"space-between"}
      >
        <Text>Filters</Text>
        <Button variant={"link"} color={"orange.500"} fontWeight={"light"}>
          Clear Filters
        </Button>
      </Box>

      <Box my={"4"}>
        <Text fontWeight={"extrabold"} mb={"2"}>
          Sort
        </Text>

        <RadioGroup onChange={setValue} value={value}>
          <Stack direction="column" gap={".25rem"}>
            <Radio
              colorScheme="orange"
              size={"md"}
              // onChange={() =>
              //   productDispatch({
              //     type: "SORT_BY_PRICE",
              //     payload: "highToLow",
              //   })
              // }
              // checked={sort === "highToLow" ? true : false}
            >
              Price High to Low
            </Radio>
            <Radio value="2" colorScheme="orange" size={"md"}>
              Price Low to High
            </Radio>
          </Stack>
        </RadioGroup>
      </Box>
    </>
  );
};

const OtherComponent = () => {
  return (
    <>
      <Box my={"4"}>
        <Text fontWeight={"extrabold"} mb={"2"}>
          Categories
        </Text>

        <Checkbox colorScheme="orange" color={"orange.500"}>
          Out of Stock
        </Checkbox>
      </Box>
    </>
  );
};

const CategoriesComponent = () => {
  const {
    state: { products },
  } = CartState();
  let CategoryList = [];
  if (products.length > 0) {
    for (let index = 0; index < 7; index++) {
      CategoryList = [...CategoryList, products[index]];
    }
  }

  return (
    <>
      <Box my={"4"}>
        <Text fontWeight={"extrabold"} mb={"2"}>
          Categories
        </Text>

        <CheckboxGroup colorScheme="orange" color={"orange.500"}>
          <Stack spacing={[1, 5]} direction={["column", "column"]}>
            {CategoryList.map((item, index) => {
              return (
                <div key={index}>
                  <Checkbox>{item.category}</Checkbox>
                </div>
              );
            })}
          </Stack>
        </CheckboxGroup>
      </Box>
    </>
  );
};
