import { Box, Text, Flex, Divider, Image } from "@chakra-ui/react";

// context Api
import { CartState } from "./Context/context";

const CartPageData = () => {
  return (
    <>
      <CartPageInitialComponent />
      <Box padding={"1vmax"}>
        <CartPageSecondryComponent />
      </Box>
    </>
  );
};

export default CartPageData;

const CartPageInitialComponent = () => {
  return (
    <>
      <Box
        display={"flex"}
        alignItems="center"
        justifyContent={"center"}
        padding={"1vmax"}
      >
        <Text fontSize={"5xl"} fontWeight={"bold"}>
          Cart Items
        </Text>
      </Box>
    </>
  );
};

const CartPageSecondryComponent = () => {
  return (
    <>
      <Flex w={"100vw"} gap={"2%"}>
        <CartItems />
        <Divider orientation="vertical" h="100px" />
        <CartPaymentPage />
      </Flex>
    </>
  );
};

const CartItems = () => {
  const {
    state: { cart },
    dispatch,
  } = CartState();
  return (
    <Flex
      boxShadow="inset 0 0 0 1px #e9e9eb"
      rounded={"md"}
      padding={"1vmax"}
      w={"60%"}
      flexDirection={"column"}
      gap={'1rem'}
    >
      {cart.map((item, index) => {
        console.log(item);
        return (
          <Flex
            boxShadow="inset 0 0 0 1px #e9e9eb"
            rounded={"md"}
            padding={"1vmax"}
            gap={"1rem"}
          >
            <Box key={index}>
              <Image
                src={item.image}
                alt={"image"}
                objectFit={"contain"}
                height={"auto"}
                width={"md"}
                rounded={"md"}
              />
            </Box>
            <Flex flexDirection={"column"} border="1px solid" w="100%">
              {/* title */}
              <Box>
                <Text>{item.name}</Text>
              </Box>
              {/* category */}
              <Box>
                <Text>{item.category}</Text>
              </Box>
                {/* price */}
                <Box>
                  <Text>  ${item.price * 2}</Text>
                  <Text></Text>
                </Box>
            </Flex>
          </Flex>
        );
      })}
    </Flex>
  );
};

const CartPaymentPage = () => {
  return (
    <>
      <Box
        w={"30vmax"}
        boxShadow="inset 0 0 0 1px #e9e9eb"
        rounded={"md"}
        padding={"1vmax"}
      >
        payment
      </Box>
    </>
  );
};
