import { Swiper, SwiperSlide } from "swiper/react";
import { Box,Heading,TExt } from "@chakra-ui/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/navigation";

// import required modules
import { Keyboard, Navigation } from "swiper";

function Herocarousel() {
  const cards = [
    {
      title: "Design Projects 1",
      text: "The project board is an exclusive resource for contract work.",
      image:
        "https://images.pexels.com/photos/38537/woodland-road-falling-leaf-natural-38537.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    },
    {
      title: "Design Projects 2",
      text: "The project board is an exclusive resource for contract work. ",
      image:
        "https://images.pexels.com/photos/301375/pexels-photo-301375.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    },
    {
      title: "Design Projects 3",
      text: "The project board is an exclusive resource for contract work.",
      image:
        "https://images.pexels.com/photos/5769337/pexels-photo-5769337.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    },
  ];
  return (
    <>
      <Box
        display={{ xl: "flex", lg: "flex", md: "flex" }}
        mx={{ xl: "10%", lg: "10%", md: "10%" }}
      >
        <Swiper
          navigation={true}
          keyboard={{
            enabled: true,
          }}
          loop={true}
          loopFillGroupWithBlank={true}
          pagination={{
            clickable: true,
          }}
          modules={[Keyboard, Navigation]}
          className="mySwiper"
        >
          {cards.map((card, index) => {
            return (
              <Box key={index}>
                <Box
                  key={index}
                  height={["40vh", "50vh", "50vh", "60vh"]}
                  position="relative"
                  backgroundPosition="center"
                  backgroundRepeat="no-repeat"
                  backgroundSize={"cover"}
                  backgroundImage={`url(${card.image})`}
                >
                  <SwiperSlide>
                    <Stack
                      // spacing={6}
                      // maxW={{ base: "90vw", md: "90vw", lg: "90vw" }}
                      display="flex"
                      flexDirection="column"
                      gap={[".5rem"]}
                      justifyContent={["center", "center", "center", "center"]}
                      alignItems="center"
                      position="absolute"
                      top={["5%", "5%", "5%", "5%"]}
                      left={["null", "5%", "15%", "15%"]}
                      right={["null", "5%", "15%", "15%"]}
                      transform="translate(0, 5%)"
                      height={["30vh", "35vh", "40vh", "45vh"]}
                      // padd
                      px={["1rem", "1rem"]}
                      // width
                      // border={[
                      //   "1px solid red",
                      //   "1px solid green",
                      //   "1px solid blue",
                      //   "1px solid yellow",
                      // ]}
                    >
                      <Heading fontSize={{ base: "3xl", md: "4xl", lg: "5xl" }}>
                        {card.title}
                      </Heading>
                      <Text
                        textAlign={["left"]}
                        fontSize={{ base: "1rem", lg: "lg" }}
                        color="white"
                      >
                        {card.text}
                      </Text>
                    </Stack>
                  </SwiperSlide>
                </Box>
              </Box>
            );
          })}
        </Swiper>
      </Box>
    </>
  );
}
export default Herocarousel;
