import React, { useState, useEfect } from "react";
import {
  Box,
  Heading,
  Link,
  Image,
  Grid,
  Text,
  Divider,
  HStack,
  Tag,
  Wrap,
  WrapItem,
  SpaceProps,
  useColorModeValue,
  Container,
  VStack,
  GridItem,
} from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";

const BlogSection = () => {
  // const [posts, setposts] = useState([]);
  //   console.log(HashnodeData.posts)
  const Item = HashnodeData.posts;
  let DisplaythreePost = [];
  if (Item.length > 0) {
    for (let index = 0; index < 3; index++) {
      DisplaythreePost = [...DisplaythreePost, Item[index]];
    }
  }

  return (
    <>
      <Box
        mx={{ xl: "10%", lg: "10%", md: "10%" }}
        my={{ xl: "2%", lg: "1.75%", md: "1.5%" }}
        // p={{ xl: "2%", lg: "2%", md: "1.5%" }}
        MinW={"25%"}
        display={{ xl: "flex", lg: "flex", md: "flex" }}
        // flexWrap={["null", "wrap", "null", "null"]}
        flexDirection={["column", "row", "row", "row"]}
        gap={5}
      >
        {DisplaythreePost.map((data) => {
          return <SingleBlog data={data} />;
        })}
      </Box>
    </>
  );
};

export default BlogSection;

const SingleBlog = ({ data }) => {
  return (
    <>
      <Box
        // px={"4%"}
        // py={"2.5%"}
        borderRadius="xl"
        // minH="100%"
        // cursor="pointer"
        border="1px solid #E7EBF0"
        bg="white"
        // boxShadow=" 0 15px 5px 0 rgba(29, 78, 216, 0.2)"
        // p="5"
        p={{ xl: "1%", lg: "1%", md: "1.5%" }}
        flexWrap="wrap"
        // m="2"
      >
        {/* <Box> */}
        <Box
          borderRadius="lg"
          overflow="hidden"
          display="flex"
          // px={{ xl: "2%", lg: "2%", md: "1.5%" }}
          py={{ xl: "2%", lg: "2%", md: "1.5%" }}
        >
          {/* <Link textDecoration="none" _hover={{ textDecoration: "none" }}> */}
          <Image
            transform="scale(1.0)"
            src={data.coverImage}
            alt={data.slug}
            objectFit="cover"
            maxH={"320px"}
            height="80%"
            w="100%"
            borderRadius={"md"}
            transition="0.3s ease-in-out"
            // _hover={{
            //   transform: "scale(1.08)",
            // }}
          />
          {/* </Link> */}
        </Box>
        <Text as="h1" fontWeight="bold" fontSize="xl" py={"2%"} mt="2">
          {data.title}
        </Text>
        <Text as="p" mt="2" py={"2%"} noOfLines={4}>
          {data.brief}
        </Text>
        {/* </Box> */}
      </Box>
    </>
  );
};
