import {
  Image,
  Box,
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbLink,
  Text,
} from "@chakra-ui/react";
// icons
import { ChevronRightIcon } from "@chakra-ui/icons";
import NatureImg from "../../Assets/temp-img/7.jpg";
import { useNavigate } from "react-router-dom";

// fonts
import "@fontsource/montserrat";
import "@fontsource/poppins";

const BannerComponent = () => {
  return (
    <>
      <Box>
        <Image
          zIndex={"1"}
          src={NatureImg}
          objectFit={"cover"}
          width="100vw"
          height={"50vh"}
          alt="banner"
        />
      </Box>
      <MessageComponent />
    </>
  );
};
export default BannerComponent;

const MessageComponent = () => {
  const Navigate = useNavigate();
  return (
    <>
      <Box
        width={{ xl: "20pc", lg: "12pc", md: "12pc", base: "9pc" }}
        height={{ xl: "20pc", lg: "12pc", md: "12pc", base: "9pc" }}
        bg={"white"}
        border="1px solid"
        borderRadius="50%"
        pos={"relative"}
        left={{ "2xl": "45vw" }}
        bottom={{ " 2xl": "18vh" }}
        zIndex={"5"}
      >
        <Box
          display="flex"
          flexDirection="column"
          alignItems="center"
          gap="1.5rem"
          pos={"relative"}
          top={{ "2xl": "8vh" }}
        >
          <Text
            fontFamily={"montserrat"}
            fontWeight={"semibold"}
            fontSize={{ xl: "2xl", lg: "xl" }}
          >
            Products
          </Text>
          <Breadcrumb
            spacing="8px"
            separator={
              <ChevronRightIcon
                color="gray.500"
                boxSize={{ xl: "10", lg: "8" }}
              />
            }
          >
            <BreadcrumbItem>
              <BreadcrumbLink onClick={() => Navigate("/")}>
                <Text
                  fontFamily={"montserrat"}
                  fontWeight={"semibold"}
                  fontSize={{ xl: "lg", lg: "md" }}
                >
                  Home
                </Text>
              </BreadcrumbLink>
            </BreadcrumbItem>

            <BreadcrumbItem>
              <BreadcrumbLink onClick={() => Navigate("/products")}>
                <Text
                  fontFamily={"montserrat"}
                  fontWeight={"semibold"}
                  fontSize={{ xl: "lg", lg: "md" }}
                >
                  Products
                </Text>
              </BreadcrumbLink>
            </BreadcrumbItem>
          </Breadcrumb>
        </Box>
      </Box>
    </>
  );
};
