import React from "react";
// Import Swiper React components
import { Swiper, SwiperSlide } from "swiper/react";

// Import Swiper styles
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

// import required modules
import { Keyboard, Navigation } from "swiper";

import {
  Box,
  IconButton,
  useBreakpointValue,
  Stack,
  Heading,
  Text,
  HStack,
} from "@chakra-ui/react";
import { BiLeftArrowAlt, BiRightArrowAlt } from "react-icons/bi";

function Herocarousel() {
  const top = useBreakpointValue({ base: "80%", md: "50%", sm: "85%" });
  const side = useBreakpointValue({ base: "5%", md: "5%" });
  const right = useBreakpointValue({ base: "5%", md: "5%" });

  const cards = [
    {
      title: "Design Projects 1",
      text: "The project board is an exclusive resource for contract work.",
      image:
        "https://images.pexels.com/photos/38537/woodland-road-falling-leaf-natural-38537.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    },
    {
      title: "Design Projects 2",
      text: "The project board is an exclusive resource for contract work. ",
      image:
        "https://images.pexels.com/photos/301375/pexels-photo-301375.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    },
    {
      title: "Design Projects 3",
      text: "The project board is an exclusive resource for contract work.",
      image:
        "https://images.pexels.com/photos/5769337/pexels-photo-5769337.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    },
  ];

  return (
    <>
      <Box
        display={["grid", "grid", "grid", "grid"]}
        gridTemplateColumns={[
          "1fr",
          ".5fr 4fr .5fr",
          ".5fr 4fr .5fr",
          ".5fr 4fr .5fr",
        ]}
      >
        <Box
          gridColumn="2/3"
          position={"relative"}
          height={["40vh", "50vh", "50vh", "60vh"]}
          overflow={"hidden"}
        >
          <Swiper
            slidesPerView={1}
            spaceBetween={30}
            keyboard={{
              enabled: true,
            }}
            loop={true}
            loopFillGroupWithBlank={true}
            pagination={{
              clickable: true,
            }}
            navigation={true}
            modules={[Keyboard, Navigation]}
          >
            {cards.map((card, index) => (
              <Box
                key={index}
                height={["40vh", "50vh", "50vh", "60vh"]}
                position="relative"
                backgroundPosition="center"
                backgroundRepeat="no-repeat"
                backgroundSize={"cover"}
                backgroundImage={`url(${card.image})`}
              >
                {/* This is the block you need to change, to customize the caption */}
                {/* //                 <Container
//                   size="container.md" */}
                {/* // position="relative" > */}
                <HStack
                  display="flex"
                  justifyContent="space-between"
                  alignItems="center"
                  pos={"absolute"}
                  left={side}
                  top={top}
                  right={right}
                >
                  {/* Left Icon */}
                  <IconButton
                    aria-label="left-arrow"
                    // colorScheme="messenger"
                    borderRadius="md"
                    zIndex={2}
                    variant="ghost"
                    color="#fff"
                    _hover={{ color: "orange", bg: "white" }}
                    //  onClick={() => slider?.slickPrev()}
                  >
                    <BiLeftArrowAlt />
                  </IconButton>

                  {/* Right Icon */}

                  <IconButton
                    aria-label="right-arrow"
                    color="#fff"
                    borderRadius="md"
                    zIndex={2}
                    variant="ghost"
                    //  onClick={() => slider?.slickNext()}
                    _hover={{ color: "orange", bg: "white" }}
                  >
                    <BiRightArrowAlt />
                  </IconButton>
                </HStack>
                <Stack
                  spacing={6}
                  maxW={{ base: "90vw", md: "90vw", lg: "90vw" }}
                  display="flex"
                  flexDirection="column"
                  gap={[".5rem"]}
                  justifyContent={["center", "center", "center", "center"]}
                  alignItems="center"
                  position="absolute"
                  top={["5%", "5%", "5%", "5%"]}
                  left={["null", "5%", "15%", "15%"]}
                  right={["null", "5%", "15%", "15%"]}
                  transform="translate(0, 5%)"
                  height={["30vh", "35vh", "40vh", "45vh"]}
                  px={["1rem", "1rem"]}
                >
                <SwiperSlide></SwiperSlide>
                  <Heading fontSize={{ base: "3xl", md: "4xl", lg: "5xl" }}>
                    {card.title}
                  </Heading>
                  <Text
                    textAlign={["left"]}
                    fontSize={{ base: "1rem", lg: "lg" }}
                    color="white"
                  >
                    {card.text}
                  </Text>
                </Stack>
              </Box>
            ))}

            {/* <SwiperSlide>Slide 1</SwiperSlide>
        <SwiperSlide>Slide 2</SwiperSlide>
        <SwiperSlide>Slide 3</SwiperSlide>
        <SwiperSlide>Slide 4</SwiperSlide> */}
          </Swiper>
        </Box>
      </Box>
    </>
  );
}

export default Herocarousel;
