import React from "react";

import {
  Box,
  Heading,
  Text,
  Stack,
  Image,
  IconButton,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  ModalCloseButton,
  Button,
} from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";
import { Eye } from "@styled-icons/feather/Eye";

function ProdContainerItem() {
  const { isOpen, onOpen, onClose } = useDisclosure();
  // const [overlay, setOverlay] = React.useState(<OverlayTwo />);
  let ShowcaseProduct = [];
  // console.log(productsArray);
  const Item = HashnodeData.posts;
  if (Item.length > 0) {
    for (let index = 0; index < 8; index++) {
      ShowcaseProduct = [...ShowcaseProduct, Item[index]];
    }
  }
  const OverlayTwo = () => (
    <ModalOverlay
      bg="black"
      // backdropFilter="auto"
      // backdropInvert="80%"
      // backdropBlur="2px"
    />
  );
  return (
    <>
      <Box
        display={{ xl: "flex", lg: "flex", md: "flex" }}
        flexWrap="wrap"
        justifyContent={"space-around"}
        mx={{ xl: "10%", lg: "10%", md: "10%" }}
        p={["1rem"]}
      >
        {
          ShowcaseProduct.map(function(item,index){
            
          })
        }
      </Box>
    </>
  );
}

const ProdContainerHeader = () => {
  return (
    <>
      <Box
        // px="1.5rem"
        py="1.5%"
        mx={{ base: "10%" }}
        display="flex"
        justifyContent={["center", "flex-start", "flex-start", "flex-start"]}
      >
        <Heading
          fontSize={["1.5rem", "1.75rem"]}
          fontWeight={["500", "600"]}
          as="h2"
          color="#2c2c2c"
        >
          <Box display={["inline-block"]}>Hot Products</Box>
          <Box
            borderBottom="2px solid #e97730"
            py={["1"]}
            pos={["relative"]}
            w={["70%", "60%"]}
            left={["4", "4"]}
            right={["4", "4"]}
          ></Box>
        </Heading>
      </Box>
    </>
  );
};

function TrendingProduct(props) {
  // eslint-disable-next-line

  return (
    <>
      <ProdContainer {...props}>
        <ProdContainerHeader />
        <ProdContainerItem />
      </ProdContainer>
    </>
  );
}

const ProdContainer = ({ children, ...props }) => {
  return <Box w="100vw">{children}</Box>;
};

export default TrendingProduct;
