import React from "react";
import { Box, Heading } from "@chakra-ui/react";
// import { faker } from "@faker-js/faker";

function TrendingProduct(props) {
  // eslint-disable-next-line

  return (
    <>
      <ProdContainer {...props}>
        <ProdContainerHeader />
        <ProdContainerItem />
      </ProdContainer>
    </>
  );
}
const ProdContainerHeader = () => {
  return (
    <>
      <Box
        // px="1.5rem"
        py="1.5%"
        mx={{ base: "10%" }}
        display="flex"
        justifyContent={["center", "flex-start", "flex-start", "flex-start"]}
      >
        <Heading
          fontSize={["1.5rem", "1.75rem"]}
          fontWeight={["500", "600"]}
          as="h2"
          color="#2c2c2c"
        >
          <Box display={["inline-block"]}>Hot Products</Box>
          <Box
            borderBottom="2px solid #e97730"
            py={["1"]}
            pos={["relative"]}
            w={["70%", "60%"]}
            left={["4", "4"]}
            right={["4", "4"]}
          ></Box>
        </Heading>
      </Box>
    </>
  );
};

const ProdContainerItem = () => {
  const ProdItems = [
    {
      // 10 items
      asin: "B09JQSLL92",
      title:
        "2021 Apple MacBook Pro (14-inch, Apple M1 Pro chip with 8‑core CPU and 14‑core GPU, 16GB RAM, 512GB SSD) - Space Gray",
      image: "https://m.media-amazon.com/images/I/61vFO3R5UNL._AC_UY218_.jpg",
      full_link: "https://www.amazon.com/dp/B09JQSLL92/?psc=1",
      prices: {
        current_price: 1999,
        previous_price: -1,
        currency: "$",
      },
      reviews: { total_reviews: 143, stars: 4.7 },
      prime: false,
      sponsored: true,
      amazon_choice: false,
      out_of_stock: false,
    },
    {
      asin: "B08N5LLDSG",
      title:
        "2020 Apple MacBook Pro with Apple M1 Chip (13-inch, 8GB RAM, 256GB SSD Storage) - Silver",
      image: "https://m.media-amazon.com/images/I/71gD8WdSlaL._AC_UY218_.jpg",
      full_link: "https://www.amazon.com/dp/B08N5LLDSG/?psc=1",
      prices: {
        current_price: 1199,
        previous_price: 1299,
        currency: "$",
      },
      reviews: {
        total_reviews: 5254,
        stars: 4.8,
      },
      prime: true,
      sponsored: true,
      amazon_choice: false,
      out_of_stock: false,
    },
    {
      asin: "B08PPZMDPK",
      title: "MacBook Pro Apple M1 chip 8GB Memory 256GB SSD Space Gray",
      image: "https://m.media-amazon.com/images/I/71DIt6oIe3L._AC_UY218_.jpg",
      full_link: "https://www.amazon.com/dp/B08PPZMDPK/?psc=1",
      prices: {
        current_price: 940,
        previous_price: 1199,
        currency: "$",
      },
      reviews: {
        total_reviews: 39,
        stars: 4.5,
      },
      prime: false,
      sponsored: false,
      amazon_choice: false,
      out_of_stock: false,
    },
    {}
  ];

  return (
    <>
      <Box>hello</Box>
    </>
  );
};
const ProdContainer = ({ children, ...props }) => {
  return <Box>{children}</Box>;
};

export default TrendingProduct;
