import {
  Image,
  Box,
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbLink,
} from "@chakra-ui/react";
import { ChevronRightIcon } from "@chakra-ui/icons";
import NatureImg from "../../Assets/temp-img/7.jpg";

const BannerComponent = () => {
  return (
    <>
      <Box>
        <Image
          zIndex={"1"}
          src={NatureImg}
          objectFit={"cover"}
          width="100vw"
          height={"50vh"}
          alt="banner"
        />
      </Box>
      <MessageComponent />
    </>
  );
};
export default BannerComponent;

const MessageComponent = () => {
  return (
    <>
      <Box
        width={{ xl: "28pc", lg: "20pc", md: "18pc", base: "15pc" }}
        height={{ xl: "28pc", lg: "20pc", md: "18pc", base: "15pc" }}
        bg={"white"}
        border="1px solid"
        borderRadius="50%"
        pos={{ xl: "relative" }}
        left={{ xl: "45vw" }}
        bottom={{ xl: "1vh" }}
        zIndex={"5"}
      >
        <Box display="flex">
          <Breadcrumb
            spacing="8px"
            separator={<ChevronRightIcon color="gray.500" />}
          >
            <BreadcrumbItem>
              <BreadcrumbLink href="#">Home</BreadcrumbLink>
            </BreadcrumbItem>

            <BreadcrumbItem>
              <BreadcrumbLink href="#">About</BreadcrumbLink>
            </BreadcrumbItem>

            <BreadcrumbItem isCurrentPage>
              <BreadcrumbLink href="#">Contact</BreadcrumbLink>
            </BreadcrumbItem>
          </Breadcrumb>
        </Box>
      </Box>
    </>
  );
};
