import { Box, Heading, Text, Stack, Image } from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";

function ProdContainerItem() {
  let ShowcaseProduct = [];
  // console.log(productsArray);
  const Item = HashnodeData.posts;
  if (Item.length > 0) {
    for (let index = 0; index < 8; index++) {
      ShowcaseProduct = [...ShowcaseProduct, Item[index]];
    }
  }
  return (
    <>
      <Box
        display={{ xl: "flex", lg: "flex", md: "flex" }}
        flexWrap="wrap"
        align
        justifyContent={'space-around'}
        mx={{ xl: "10%", lg: "10%", md: "10%" }}
        py={{ xl: "4%", lg: "4%" }}
        pt={{ sm: "1rem", md: "1rem" }}
        // border="1px solid"
        p={["1rem"]}
      >
        {ShowcaseProduct.map(function (Item, index) {
          return (
            <>
              <Box
                role={"group"}
                // p={6}
                maxW={["100% ", "250px", "250px", "250px"]}
                rounded={"md"}
                overflow={"hidden"}
                // pos={"relative"}
                // mb={"2%"}
                border="1px solid lightgrey"
                zIndex={1}
                mb={["1rem"]}
              >
                <Box
                  overflow={"hidden"}
                  // rounded={"md"}
                  h={"210px"}
                  w={"100%"}
                  bg={"gray.100"}
                  // mt={-6}
                  // mx={-6}
                  // mb={6}
                  pos={"relative"}
                >
                  <Image
                    // rounded={"md"}
                    height={"100%"}
                    width={"100%"}
                    objectFit={"cover"}
                    src={Item.coverImage}
                  />
                </Box>
                <Stack mt="3">
                  <Text
                    color={"gray.500"}
                    fontSize={"sm"}
                    textTransform={"uppercase"}
                  >
                    {Item.title}
                  </Text>
                  <Stack direction={"row"} align={"center"}>
                    <Text fontWeight={800} fontSize={"xl"}>
                      $57
                    </Text>
                    <Text textDecoration={"line-through"} color={"gray.600"}>
                      $199
                    </Text>
                  </Stack>
                </Stack>
              </Box>
            </>
          );
        })}
      </Box>
    </>
  );
}

const ProdContainerHeader = () => {
  return (
    <>
      <Box
        // px="1.5rem"
        py="1.5%"
        mx={{ base: "10%" }}
        display="flex"
        justifyContent={["center", "flex-start", "flex-start", "flex-start"]}
      >
        <Heading
          fontSize={["1.5rem", "1.75rem"]}
          fontWeight={["500", "600"]}
          as="h2"
          color="#2c2c2c"
        >
          <Box display={["inline-block"]}>Hot Products</Box>
          <Box
            borderBottom="2px solid #e97730"
            py={["1"]}
            pos={["relative"]}
            w={["70%", "60%"]}
            left={["4", "4"]}
            right={["4", "4"]}
          ></Box>
        </Heading>
      </Box>
    </>
  );
};

function TrendingProduct(props) {
  // eslint-disable-next-line

  return (
    <>
      <ProdContainer {...props}>
        <ProdContainerHeader />
        <ProdContainerItem />
      </ProdContainer>
    </>
  );
}

const ProdContainer = ({ children, ...props }) => {
  return <Box w="100vw">{children}</Box>;
};

export default TrendingProduct;
