import React from "react";
import {
  Box,
  Flex,
  Text,
  Button,
  Stack,
  useColorMode,
  Image,
  HStack,
  Grid,
} from "@chakra-ui/react";
import { BiMenu } from "react-icons/bi";
import { AiOutlineClose } from "react-icons/ai";
import { ColorModeSwitcher } from "../ColorModeSwitcher";

// icon
import Bag from "../Image/Bag.png";
import User from "../Image/User.png";
import Menu from "../Image/Menu.png";
import Close from "../Image/Close.png";

// import { NavHashLink } from 'react-router-hash-link';

// https://github.com/dimitrisraptis96/chakra-ui-navbar/blob/main/src/components/Header.js
// https://raptis.wtf/freebies/color-pairs

function Navbar(props) {
  // eslint-disable-next-line
  const { colorMode, toggleColorMode } = useColorMode();
  const [isOpen, setIsOpen] = React.useState(false);

  const toggle = () => setIsOpen(!isOpen);

  return (
    <NavBarContainer
      {...props}
      bg={colorMode === "light" ? "bodyBackground" : ""}
    >
      <HStack>
        <a href="/">
          <Text as="h2" fontWeight="500" fontSize="2xl">
            Logo
          </Text>
        </a>
      </HStack>
      <MenuLinks isOpen={isOpen} />
      <IconComponent />

      <MenuToggle toggle={toggle} isOpen={isOpen} />
    </NavBarContainer>
  );
}

const MenuLinks = ({ isOpen }) => {
  return (
    <HStack
      // flexDirection={{ sm: "column" }}
      justifyContent="space-between"
      border="1px solid red"
      display={["none", "none", "flex", "flex"]}
    >
      <a href="/">
        <Text display="block" fontWeight="500">
          Home
        </Text>
      </a>

      <a href="/#Products">
        <Text display="block" fontWeight="500">
          Products
        </Text>
      </a>

      <a href="/#Training">
        <Text display="block" fontWeight="500">
          {/* Training and Consultation */}
          Training
        </Text>
      </a>

      <a href="/#5s-Warm-Up">
        <Text display="block" fontWeight="500">
          5s Warm Up
        </Text>
      </a>
    </HStack>
  );
};

const MenuToggle = ({ toggle, isOpen }) => {
  return (
    <Box
      // display={{ base: "block", md: "none" }}
      display={["flex", "flex", "none", "none"]}
      onClick={toggle}
    >
      {isOpen ? <AiOutlineClose size={40} /> : <BiMenu size={40} />}
    </Box>
  );
};

const IconComponent = () => {
  return (
    <>
      <Box
        display={["flex"]}
        // flexDirection={["row"]}
        // border="1px solid"
        gap={[".5rem", ".5rem"]}
        justifyContent={{
          xl: "flex-end",
          lg: "flex-end",
          md: "flex-end",
          // sm: "flex-end",
        }}
      >
        <Box>
          <Image src={Bag} height="30px" width="auto" />
        </Box>
        <Box>
          <Image src={User} height="30px" width="auto" />
        </Box>
      </Box>
    </>
  );
};
const NavBarContainer = ({ children, ...props }) => {
  return (
    <Box
      as="nav"
      align="center"
      justify="space-between"
      wrap="wrap"
      w="100vw"
      p="1%"
      display={{
        sm: "grid",
        md: "grid",
        lg: "grid",
        xl: "grid",
      }}
      gridTemplateColumns={{
        sm:'1fr 1fr',
        md: ".5fr 1fr .5fr",
        lg: ".5fr 1fr .5fr",
        xl: ".5fr 1fr .5fr",
      }}
      {...props}
    >
      {children}
    </Box>
  );
};
export default Navbar;
