import React from "react";
import { ChakraProvider, Image, Box } from "@chakra-ui/react";
import { Navigation, Keyboard, Autoplay } from "swiper";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

import "./carousel.css";

// images
import FootSole from "../Image/temp-img/FootSole.png";
import tape from "../Image/temp-img/tape.png";
import TShirt from "../Image/temp-img/TShirt.png";
import YellowTape from "../Image/temp-img/YellowTape.png";

const Herocarousel = () => {
  return (
    <>
      <ChakraProvider>
        <Swiper
          style={{
            // "--swiper-pagination-color": "yellow",
            "--swiper-theme-color": "lightgrey",
            "--swiper-navigation-size": " 2rem",
            border: "1px solid",
            display: "flex",
            justifyContent: "center",
          }}
          slidesPerView={1}
          spaceBetween={20}
          loop={true}
          pagination={{
            clickable: true,
          }}
          autoplay={{
            delay: 2500,
            disableOnInteraction: false,
          }}
          keyboard={{
            enabled: true,
          }}
          navigation={true}
          modules={[Navigation, Keyboard, Autoplay]}
          className="mySwiper"
        >
          <SwiperSlide>
            <Box
             px={{sm:'.75rem',md:'1rem',lg:'1.25rem',xl:'1.5rem'}}
              padding="1rem"
              display="flex"
              justifyContent="center"
            >
              <Image objectFit="cover" src={FootSole} h="auto" w="50%" />
            </Box>
          </SwiperSlide>

          <SwiperSlide>
            <Box
             px={{sm:'.75rem',md:'1rem',lg:'1.25rem',xl:'1.5rem'}}
              padding="1rem"
              display="flex"
              justifyContent="center"
            >
              <Image objectFit="cover" src={tape} h="auto" w="50%" />
            </Box>
          </SwiperSlide>

          <SwiperSlide>
            <Box
             px={{sm:'.75rem',md:'1rem',lg:'1.25rem',xl:'1.5rem'}}
              padding="1rem"
              display="flex"
              justifyContent="center"
            >
              <Image objectFit="cover" src={TShirt} h="auto" w="50%" />
            </Box>
          </SwiperSlide>

          <SwiperSlide>
            <Box
             px={{sm:'.75rem',md:'1rem',lg:'1.25rem',xl:'1.5rem'}}
              padding="1rem"
              display="flex"
              justifyContent="center"
            >
              <Image objectFit="cover" src={YellowTape} h="auto" w="50%" />
            </Box>
          </SwiperSlide>
        </Swiper>
      </ChakraProvider>
    </>
  );
};

export default Herocarousel;
