import React from "react";
import {
  Divider,
  Text,
  Flex,
  Box,
  Button,
  Tag,
  TagLabel,
  TagLeftIcon,
  Accordion,
  AccordionItem,
  AccordionButton,
  AccordionPanel,
  AccordionIcon,
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  Checkbox,
  CheckboxGroup,
  Radio,
  Stack,
  RadioGroup,
} from "@chakra-ui/react";

import { ChevronDownIcon, StarIcon } from "@chakra-ui/icons";
import { CartState } from "../Context/context";

const FilterCard = () => {
  return (
    <>
      <Flex flexDirection={"column"}>
        <SortComponent />
        <Divider />
        <CategoriesComponent />
        {/* <FilterComponent /> */}
      </Flex>
    </>
  );
};

export default FilterCard;

const SortComponent = () => {
  // for sorting only
  const [value, setValue] = React.useState("");
  return (
    <>
      <Box
        my={"4"}
        display={"flex"}
        gap={"10vmax"}
        w={"100%"}
        alignItems={"center"}
        justifyContent={"space-between"}
      >
        <Text>Filters</Text>
        <Button variant={"link"} color={"orange.500"} fontWeight={"light"}>
          Clear Filters
        </Button>
      </Box>

      <Box my={"4"}>
        <Text fontWeight={"extrabold"} mb={"2"}>
          Sort
        </Text>

        <RadioGroup onChange={setValue} value={value}>
          <Stack direction="column" gap={".25rem"}>
            <Radio value="1" colorScheme="orange" size={"md"}>
              Price High to Low
            </Radio>
            <Radio value="2" colorScheme="orange" size={"md"}>
              Price Low to High
            </Radio>
          </Stack>
        </RadioGroup>
      </Box>
    </>
  );
};

const FilterComponent = () => {
  const PriceList = [
    "Under 149",
    "Under 199",
    "Under 249",
    "Under 299",
    "Under 349",
    "Under 399",
    "Under 449",
    "Under 499",
  ];

  const {
    state: { products },
  } = CartState();

  return (
    <Flex padding=".5rem" flexDirection={"column"} gap=".5rem">
      <Flex flexDirection={"column"} gap=".5rem">
        {/* // as={Button}
        // bg={"white"}
        // border="1px solid lightgrey" // color={"blackAlpha.900"}
        // rightIcon={<ChevronDownIcon />}
        // > Price display={"flex"} */}
        {/* flexWrap="wrap" flexDirection="column" gap="1rem" marginRight="1rem"
        maxH="30vh" padding=".5rem" // > */}

        <Accordion allowMultiple>
          <AccordionItem>
            <h2>
              <AccordionButton>
                <Box textAlign="left" flex={"1"}>
                  Price
                </Box>
                <AccordionIcon />
              </AccordionButton>
            </h2>
            <AccordionPanel border={"1px solid"}>
              <Box
                w={"50%"}
                display={"flex"}
                flexWrap={"wrap"}
                // flexDirection={"column"}
                justifyContent={"space-around"}
                gap={".5rem"}
              >
                {PriceList.map((item, index) => {
                  return (
                    <Tag variant="outline" rounded={"full"}>
                      <TagLabel>{item}</TagLabel>
                    </Tag>
                  );
                })}
              </Box>
            </AccordionPanel>
          </AccordionItem>
        </Accordion>

        {/* {PriceList.map((item, index) => {
                return (
                  <MenuItem key={index}>
                    <Tag variant="outline" rounded={"full"}>
                      <TagLabel>{item}</TagLabel>
                    </Tag>
                  </MenuItem>
                );
              })} */}

        <Menu>
          <MenuButton
            as={Button}
            bg={"white"}
            border="1px solid lightgrey"
            color={"blackAlpha.900"}
            rightIcon={<ChevronDownIcon />}
          >
            Rating
          </MenuButton>
          <MenuList>
            {[1, 2, 3, 4, 5].map((item, index) => {
              return (
                <MenuItem
                  display={"flex"}
                  flexWrap="wrap"
                  flexDirection="column"
                  gap="1rem"
                  marginRight="1rem"
                  maxH="30vh"
                  key={index}
                >
                  <Tag
                    variant="subtle"
                    colorScheme="blackAlpha"
                    rounded={"md"}
                    padding=".5rem"
                  >
                    <TagLeftIcon boxSize="12px" as={StarIcon} />
                    <TagLabel>{index}</TagLabel>
                  </Tag>
                </MenuItem>
              );
            })}
          </MenuList>
        </Menu>
        {/* Clear Out */}
        <Button display="flex" colorScheme="red">
          Clear Filters
        </Button>
      </Flex>
    </Flex>
  );
};

const CategoriesComponent = () => {
  const {
    state: { products },
  } = CartState();
  return (
    <>
      <Box my={"4"}>
        <Text fontWeight={"extrabold"} mb={"2"}>
          Categories
        </Text>

        <CheckboxGroup colorScheme="orange" color={"orange.500"}>
          <Stack spacing={[1, 5]} direction={["column", "column"]}>
            {products.map((item, index) => {
              return <>
              
              </>
            })}
            {/* <Checkbox value="naruto">Naruto</Checkbox> */}
          </Stack>
        </CheckboxGroup>
      </Box>
    </>
  );
};
