import React, { useState, useEffect } from "react";
import Product from "./product.json";
import {
  Box,
  Image,
  Text,
  useColorModeValue,
  Heading,
  Stack,
} from "@chakra-ui/react";

const CartItems = () => {
  const [Prod, setProd] = useState([]);
  const FetchPost = () => {
    let CalculatedProd = [];
    if (Product.length > 0) {
      for (let index = 0; index < 12; index++) {
        CalculatedProd = [...CalculatedProd, Product[index]];
      }
    }
    // console.log(CalculatedProd);

    setProd(CalculatedProd);
  };
  useEffect(() => {
    return () => {
      FetchPost();
    };
  }, []);

  return (
    <>
      <Box borderRadius={"md"} padding={"1rem"} border="1px solid" bg={"white"}>
        {Prod.map((item, index) => {
          return (
            <Box key={index} display={"flex"}>
              <Box
                border="1px solid"
                rounded={"md"}
                // mt={-12}
                // pos={"relative"}
                // height={"230px"}
                _after={{
                  transition: "all .3s ease",
                  content: '""',
                  w: "full",
                  h: "full",
                  // pos: "absolute",
                  top: 5,
                  left: 0,
                  backgroundImage: `url(${item.image})`,
                  // filter: "blur(15px)",
                  zIndex: -1,
                }}
                _groupHover={{
                  _after: {
                    filter: "blur(20px)",
                  },
                }}
              >
                <Image
                  rounded={"md"}
                  height={230}
                  width={282}
                  objectFit={"cover"}
                  src={item.images}
                />
              </Box>
              <Stack pt={10} align={"center"}>
                <Text
                  color={"gray.500"}
                  fontSize={"sm"}
                  textTransform={"uppercase"}
                >
                  {item.category.name}
                </Text>
                <Heading fontSize={"2xl"} fontFamily={"body"} fontWeight={500}>
                  {item.title}
                </Heading>
                <Stack direction={"row"} align={"center"}>
                  <Text fontWeight={800} fontSize={"xl"}>
                    ${item.price}
                  </Text>
                  <Text textDecoration={"line-through"} color={"gray.600"}>
                  {item.price}*
                  </Text>
                </Stack>
              </Stack>
            </Box>
          );
        })}
        {/* <ProductSimple /> */}
      </Box>
    </>
  );
};

export default CartItems;

const IMAGE =
  "https://images.unsplash.com/photo-1518051870910-a46e30d9db16?ixlib=rb-1.2.1&ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&auto=format&fit=crop&w=1350&q=80";

function ProductSimple() {
  return (
    // <Center py={12}>
    // <Box
    //   role={"group"}
    //   p={6}
    //   maxW={"330px"}
    //   w={"full"}
    //   bg={useColorModeValue("white", "gray.800")}
    //   boxShadow={"2xl"}
    //   rounded={"lg"}
    //   pos={"relative"}
    //   zIndex={1}
    // >
    <></>
    // </Box>
    // </Center>
  );
}
