import React, { useState, useEffect } from "react";
import { Grid, GridItem, Text, Box, Image } from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";

const BlogSection = () => {
  return (
    <>
      <Grid
        templateRows="1fr"
        placeItems="center"
        p="1rem"
        id="blogs&articles"
        // bg="#e9f8f3"
      >
        <BlogComponent />
      </Grid>
    </>
  );
};

const BlogComponent = () => {
  const [posts, setposts] = useState([]);

  let DisplaythreePost = [];
  // async await function

  function FetchPostData() {
    Object.entries(HashnodeData).map(function (item, indexes) {
      if (item[1].length > 0) {
        for (let index = 0; index < 3; index++) {
          DisplaythreePost = [...DisplaythreePost, item[1][index]];
        }
      }
      setposts(DisplaythreePost);
    });
  }

  useEffect(() => {
    FetchPostData();
  }, []);
  return (
    <>
      <Grid templateColumns="350px 350px 350px" columnGap="2rem">
        {posts.map(function (post, index) {
          return (
            <>
              <li key={index} style={{ listStyle: "none" }}>
                <Grid
                  templateRows="1fr"
                  placeContent="center"
                  border="1px solid #E7EBF0"
                  _hover={{
                    boxShadow:
                      "rgba(60, 64, 67, 0.3) 0px 1px 2px 0px, rgba(60, 64, 67, 0.15) 0px 2px 6px 2px;",
                    // transition:
                    transition: ".5s ease-in-out",
                  }}
                  borderRadius=".5rem"
                  bg="white"
                  key={post.id}
                >
                  <DefaultApiprops post={post} key={index} />
                </Grid>
              </li>
            </>
          );
        })}
      </Grid>
    </>
  );
};

function DefaultApiprops({ post }) {
  return (
    <>
      {/* 1st column */}
      <Grid templateRows="1fr .25fr .50fr .10fr" p=".5rem">
        <GridItem gridRow="1/2" justifySelf="center" mt=".5rem">
          <Image
            h="200px"
            w="320px"
            borderRadius=".5rem"
            objectFit="cover"
            src={post.coverImage}
            alt={post.title}
          />
        </GridItem>

        <GridItem
          gridRow="2/3"
          color="black"
          cursor="pointer"
          placeSelf="center"
          p=".5rem"
        >
          {/* <Link to={`/blog/${post.slug}`}> */}
          <Text fontWeight="700" color="#111827" textDecoration="none">
            {post.title}
          </Text>
          {/* </Link> */}
        </GridItem>

        <GridItem p=".5rem" gridRow="3/4">
          <Text color="#424b5a" lineHeight="1.3rem" fontWeight="400">
            {post.brief}
          </Text>
        </GridItem>
        <GridItem p=".5rem" gridRow="4/5">
          <Box d="flex" justifyContent="flex-end">
            {/* <Link to={`/blog/${post.slug}`}> */}
            <Text>Read More</Text>
            {/* </Link> */}
          </Box>
        </GridItem>
      </Grid>
    </>
  );
}

export default BlogSection;
