import React from "react";
import {
  FormControl,
  Divider,
  Text,
  Flex,
  Box,
  Button,
  Tag,
} from "@chakra-ui/react";
import {
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  // MenuItemOption,
  // MenuGroup,
  // MenuOptionGroup,
  // MenuDivider,
} from "@chakra-ui/react";
import { ChevronDownIcon } from "@chakra-ui/icons";

import { CartState } from "../Context/context";

const FilterCard = () => {
const PriceList = ['Under 149','Under 199','Under 249','Under 149','Under 349','Under 149','Under 449','Under 149','Under 149','Under 149','Under 149','Under 149','Under 149',]

  return (
    <>
      <Flex flexDirection={"column"}>
        <SortComponent />
        <FilterComponent />
      </Flex>
    </>
  );
};

export default FilterCard;

const SortComponent = () => {
  return (
    <>
      <FormControl p=".5rem">
        <Box
          display="flex"
          alignItems="center"
          rounded={"md"}
          border="1px solid lightgrey"
          p=".75rem"
        >
          <Text>Sort By :</Text>
          <select border="none" outline="none">
            <option border="none" bg="transparent" outline="none">
              <Text fontWeight={"extrabold"}>High to Low</Text>
            </option>
            <option border="none" bg="transparent" outline="none">
              <Text fontWeight={"extrabold"}>Low to High</Text>
            </option>
            <option border="none" bg="transparent" outline="none">
              <Text fontWeight={"extrabold"}>Rating</Text>
            </option>
          </select>
        </Box>
      </FormControl>
    </>
  );
};

const FilterComponent = () => {
  return (
    <Flex padding=".5rem" flexDirection={"column"} gap=".5rem">
      <Box padding=".5rem">
        <Text fontWeight={"extrabold"}>FILTERS</Text>
        <Text fontSize="sm" color={"grey"}>
          1000+ Products
        </Text>
      </Box>
      <Divider colorScheme={"red"} />
      {/* pricing */}
      <Menu>
        <MenuButton
          as={Button}
          border="1px solid lightgrey"
          variant="ghost"
          color={"blackAlpha.900"}
          rightIcon={<ChevronDownIcon />}
        >
          Price
        </MenuButton>
        <Box>
          <MenuList>
            <MenuItem>Download</MenuItem>
            <MenuItem>Create a Copy</MenuItem>
            <MenuItem>Mark as Draft</MenuItem>
            <MenuItem>Delete</MenuItem>
            <MenuItem>Attend a Workshop</MenuItem>
          </MenuList>
        </Box>
      </Menu>
      {/* Rating */}
      <Menu>
        <MenuButton
          as={Button}
          // bg={"white"}
          border="1px solid lightgrey"
          variant="ghost"
          color={"blackAlpha.900"}
          rightIcon={<ChevronDownIcon />}
        >
          Rating
        </MenuButton>
        <Box>
          <MenuList>
            <MenuItem>Download</MenuItem>
            <MenuItem>Create a Copy</MenuItem>
            <MenuItem>Mark as Draft</MenuItem>
            <MenuItem>Delete</MenuItem>
            <MenuItem>Attend a Workshop</MenuItem>
          </MenuList>
        </Box>
      </Menu>
    </Flex>
  );
};
