import React from "react";
import { Box, Image, Text } from "@chakra-ui/react";
import { CartState } from "../Context/context";
import Header from "../../Component/Header";
import error from "../../Assets/404/woman.png";

const CartPage = () => {
  const {
    state: { products, cart },
    dispatch,
  } = CartState();
  return (
    <>
      <Header />
      {cart.length === 0 ? (
        <Box >
          <Image
            src={error}
            boxSize={"40%"}
            position={"relative"}
            left={"10%"}
            // mt={"2"}
            alt={"woman"}
          />
          <Text
          transition={'ease-in'}
          
            pos={"absolute"}
            left={"40%"}
            bottom={"1%"}
            fontSize={"9xl"}
            fontWeight={"extrabold"}
          >
            Cart is empty
          </Text>
        </Box>
      ) : (
        <h1>item are there</h1>
      )}
    </>
  );
};

export default CartPage;
