import React, { useState } from "react";
import {
  Box,
  Heading,
  Link,
  Image,
  Text,
  Divider,
  HStack,
  Tag,
  Wrap,
  WrapItem,
  SpaceProps,
  useColorModeValue,
  Container,
  VStack,
} from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";

const BlogSection = () => {
  const [posts, setposts] = useState([]);
  //   console.log(HashnodeData.posts)
  const Item = HashnodeData.posts;
  let DisplaythreePost = [];
  if (Item.length > 0) {
    for (let index = 0; index < 3; index++) {
      DisplaythreePost = [...DisplaythreePost, Item[index]];
      // console.log("succed", DisplaythreePost);
    }
  }
  setposts(DisplaythreePost);

  return (
    <>
      {posts.map(function (item, index) {
        return (
            <Box>
            </Box>
        );
      })}
    </>
  );
};

export default BlogSection;
