// images
// import FootSole from "../Image/temp-img/FootSole.png";
// import tape from "../Image/temp-img/tape.png";
// import TShirt from "../Image/temp-img/TShirt.png";
// import YellowTape from "../Image/temp-img/YellowTape.png";

import React from "react";
import {
  Box,
  IconButton,
  useBreakpointValue,
  Stack,
  Heading,
  // Grid,
  // GridItem,
  Text,
  // Container,
  HStack,
} from "@chakra-ui/react";
// Here we have used react-icons package for the icons
import { BiLeftArrowAlt, BiRightArrowAlt } from "react-icons/bi";
// And react-slick as our Carousel Lib
import Slider from "react-slick";

// Settings for the slider
const settings = {
  dots: true,
  arrows: false,
  fade: true,
  infinite: true,
  autoplay: true,
  speed: 500,
  autoplaySpeed: 5000,
  slidesToShow: 1,
  slidesToScroll: 1,
};

function Herocarousel() {
  // As we have used custom buttons, we need a reference variable to
  // change the state
  const [slider, setSlider] = React.useState(<Slider />);

  // These are the breakpoints which changes the position of the
  // buttons as the screen size changes
  const top = useBreakpointValue({ base:"45%",md:'50%' });
  const side = useBreakpointValue({ base: "5%", md: "5%" });
  const right = useBreakpointValue({ base: "5%", md: "5%" });

  // This list contains all the data for carousels
  // This can be static or loaded from a server
  const cards = [
    {
      title: "Design Projects 1",
      text: "The project board is an exclusive resource for contract work. It's perfect for freelancers, agencies, and moonlighters.",
      image:
        "https://images.pexels.com/photos/38537/woodland-road-falling-leaf-natural-38537.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    },
    {
      title: "Design Projects 2",
      text: "The project board is an exclusive resource for contract work. It's perfect for freelancers, agencies, and moonlighters.",
      image:
        "https://images.pexels.com/photos/301375/pexels-photo-301375.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    },
    {
      title: "Design Projects 3",
      text: "The project board is an exclusive resource for contract work. It's perfect for freelancers, agencies, and moonlighters.",
      image:
        "https://images.pexels.com/photos/5769337/pexels-photo-5769337.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    },
  ];

  return (
    <>
      <Box
      
        display={["grid", "grid", "grid", "grid"]}
        gridTemplateColumns={[
          "1fr",
          ".5fr 4fr .5fr",
          ".5fr 4fr .5fr",
          ".5fr 4fr .5fr",
        ]}
      >
        <Box
          gridColumn="2/3"
          position={"relative"}
          height={["40vh", "50vh", "60vh", "70vh"]}
          width={"full"}
          overflow={"hidden"}
        >
          {/* hello */}
          {/* CSS files for react-slick */}
          <link
            rel="stylesheet"
            type="text/css"
            charSet="UTF-8"
            href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick.min.css"
          />
          <link
            rel="stylesheet"
            type="text/css"
            href="https://cdnjs.cloudflare.com/ajax/libs/slick-carousel/1.6.0/slick-theme.min.css"
          />

          {/* Slider */}
          <Slider {...settings} ref={(slider) => setSlider(slider)}>
            {cards.map((card, index) => (
              <Box
                key={index}
                height={["40vh", "40vh", "50vh", "60vh"]}
                position="relative"
                backgroundPosition="center"
                backgroundRepeat="no-repeat"
                backgroundSize={""}
                
                backgroundImage={`url(${card.image})`}
              >
                {/* This is the block you need to change, to customize the caption */}
                {/* <Container
                  size="container.md" */}
                {/* // position="relative" > */}
                <HStack
                  display="flex"
                  justifyContent="space-between"
                  alignItems="center"
                  pos={"absolute"}
                  left={side}
                  top={top}
                  right={right}
                >
                  {/* Left Icon */}
                  <IconButton
                    aria-label="left-arrow"
                    colorScheme="messenger"
                    borderRadius="md"
                    zIndex={2}
                    variant="ghost"
                    // transform={"translate(0%, 50%)"}
                    onClick={() => slider?.slickPrev()}
                  >
                    <BiLeftArrowAlt />
                  </IconButton>

                  {/* Right Icon */}
                  <IconButton
                    aria-label="right-arrow"
                    colorScheme="messenger"
                    borderRadius="md"
                    zIndex={2}
                    variant="ghost"
                    // transform={"translate(0%, 50%)"}
                    onClick={() => slider?.slickNext()}
                  >
                    <BiRightArrowAlt />
                  </IconButton>
                </HStack>
                {/* </Container> */}
              </Box>
            ))}
          </Slider>
        </Box>
      </Box>
    </>
  );
}

export default Herocarousel;
