import React from "react";
import { Box, Image, Text } from "@chakra-ui/react";
import { CartState } from "../Context/context";
import Header from "../../Component/Header";
import error from "../../Assets/404/robot.png";

const CartPage = () => {
  const {
    state: { products, cart },
    dispatch,
  } = CartState();
  return (
    <>
      <Header />
      {cart.length === 0 ? (
        <Box>
          <Image
            src={error}
            boxSize={"30%"}
            margin={"auto"}
            mt={"2"}
            alt={"woman"}
          />
          <Text pos={'absolute'} left={'65%'} bottom={'10%'} fontSize={'9xl'} fontWeight={'extrabold'}> Cart is empty</Text>
        </Box>
      ) : (
        <h1>item are there</h1>
      )}
    </>
  );
};

export default CartPage;
