import React, { useState } from "react";
import {
  Divider,
  Text,
  Flex,
  Box,
  Button,
  Checkbox,
  CheckboxGroup,
  Radio,
  Stack,
  RadioGroup,
} from "@chakra-ui/react";

// for mobile devices
import {
  Menu,
  MenuButton,
  MenuList,
  MenuItem,
  MenuItemOption,
  MenuGroup,
  MenuOptionGroup,
  MenuDivider,
} from "@chakra-ui/react";
import SwapVertIcon from "@mui/icons-material/SwapVert";

import { CartState } from "../Context/context";

const FilterCard = () => {
  return (
    <>
      <Box display={["flex"]}>
        <SortComponent />
        {/* <Divider />
        <CategoriesComponent />
        <Divider />
        <OtherComponent /> */}
      </Box>
    </>
  );
};

export default FilterCard;

const SortComponent = () => {
  // for sorting only
  const [value, setValue] = React.useState("");

  // render prod by category
  let {
    state: { products },
    productState: { sort, byStock },
    productDispatch,
  } = CartState();

  return (
    <>
      {/* display none at small device default */}
      <Box
        my={"4"}
        display={"none"}
        gap={"10vmax"}
        w={"100%"}
        alignItems={"center"}
        justifyContent={"space-between"}
      >
        <Text>Filters</Text>
        <Button
          variant={"link"}
          color={"orange.500"}
          fontWeight={"light"}
          onClick={() =>
            productDispatch({
              type: "CLEAR_FILTERS",
            })
          }
        >
          Clear Filters
        </Button>
      </Box>

      <Box my={"4"}>
        <Text fontWeight={"extrabold"} mb={"2"} display={["none"]}>
          Sort
        </Text>
        <Menu>
          <MenuButton
            bg={"transparent"}
            as={Button}
            _focus={{ background: "transparent" }}
            leftIcon={<SwapVertIcon />}
          >
            Sort
          </MenuButton>
          <MenuList pos={["absolute"]}>
            <MenuItem>
              <RadioGroup onChange={setValue} value={value}>
                {/* <Stack direction="column" gap={".25rem"}>
            <Radio
              value="1"
              colorScheme="orange"
              size={"md"}
              onChange={() =>
                productDispatch({
                  type: "SORT_BY_PRICE",
                  payload: "highToLow",
                })
              }
              checked={sort === "highToLow" ? true : false}
            >
              Price High to Low
            </Radio>
            <Radio
              value="2"
              colorScheme="orange"
              size={"md"}
              onChange={() =>
                productDispatch({
                  type: "SORT_BY_PRICE",
                  payload: "lowToHigh",
                })
              }
              checked={sort === "lowToHigh" ? true : false}
            >
              Price Low to High
            </Radio>
          </Stack> */}
              </RadioGroup>
            </MenuItem>
          </MenuList>
        </Menu>
      </Box>
    </>
  );
};

const OtherComponent = () => {
  let {
    state: { products },
    productState: { byStock, category },
    productDispatch,
  } = CartState();
  const [RenderCategory, setRenderCategory] = useState(category);

  return (
    <>
      <Box my={"4"}>
        <Text fontWeight={"extrabold"} mb={"2"}>
          Other
        </Text>

        <Checkbox
          colorScheme="orange"
          color={"orange.500"}
          onChange={() =>
            productDispatch({
              type: "FILTER_BY_STOCK",
            })
          }
          checked={byStock}
        >
          Out of Stock
        </Checkbox>
      </Box>
    </>
  );
};

const CategoriesComponent = () => {
  const {
    state: { products },
    productState: { byStock, category },
    productDispatch,
  } = CartState();

  let CategoryList = [];
  if (products.length > 0) {
    for (let index = 0; index < 5; index++) {
      CategoryList = [...CategoryList, products[index]];
    }
  }

  return (
    <>
      <Box my={"4"}>
        <Text fontWeight={"extrabold"} mb={"2"}>
          Categories
        </Text>

        <CheckboxGroup colorScheme="orange" color={"orange.500"}>
          <Stack spacing={[1, 5]} direction={["column", "column"]}>
            {CategoryList.map((prodcat, prodindex) => {
              return (
                <div key={prodindex}>
                  <Checkbox
                    onChange={() => {
                      productDispatch({
                        type: "FILTER_BY_CATEGORY",
                      });
                    }}
                  >
                    {prodcat.category}
                  </Checkbox>
                </div>
              );
            })}
          </Stack>
        </CheckboxGroup>
      </Box>
    </>
  );
};
