import React, { useState } from "react";
import {
  Box,
  Flex,
  Text,
  Grid,
  GridItem,
  Stack,
  useColorMode,
} from "@chakra-ui/react";
// icons
import { BiMenu } from "react-icons/bi";
import { AiOutlineClose } from "react-icons/ai";
import { HiOutlineUserCircle } from "react-icons/hi";
import { IoBagHandleOutline } from "@react-icons/all-files/i/FaBeer";

import { NavHashLink } from "react-router-hash-link";

function Navbar(props) {
  // eslint-disable-next-line
  const { colorMode, toggleColorMode } = useColorMode();
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => setIsOpen(!isOpen);

  return (
    <NavBarContainer {...props}>
      <NavHashLink to="/">
        <Text as="h1" fontWeight="500" fontSize="4xl">
          Logo
        </Text>
      </NavHashLink>

      <MenuToggle toggle={toggle} isOpen={isOpen} />
      <MenuLinks isOpen={isOpen} />
      <IconComponent isOpen={isOpen} toggle={toggle} />
    </NavBarContainer>
  );
}

const MenuToggle = ({ toggle, isOpen }) => {
  return (
    <Box display={{ base: "block", md: "none" }} onClick={toggle}>
      {/* {isOpen ? <AiOutlineClose size={40} /> : <BiMenu size={40} />} */}
      <HiOutlineUserCircle />
      <IoBagHandleOutline />
    </Box>
  );
};

const MenuLinks = ({ isOpen }) => {
  return (
    <Box
      display={{ base: isOpen ? "block" : "none", md: "block" }}
      flexBasis={{ base: "100%", md: "auto" }}
    >
      <Stack
        spacing={8}
        align="flex-end"
        justify={["flex-end", "space-around", "flex-end", "center"]}
        direction={["column", "row", "row", "row"]}
        pt={[4, 4, 0, 0]}
      >
        <NavHashLink to="/">
          <Text display="block" fontWeight="700">
            Home
          </Text>
        </NavHashLink>

        <NavHashLink to="/#Products">
          <Text display="block" fontWeight="700">
            Products
          </Text>
        </NavHashLink>

        <NavHashLink to="/#Training">
          <Text display="block" fontWeight="700">
            {/* Training and Consultation */}
            Training
          </Text>
        </NavHashLink>

        <NavHashLink to="/#5s-Warm-Up">
          <Text display="block" fontWeight="700">
            5s Warm Up
          </Text>
        </NavHashLink>
      </Stack>
    </Box>
  );
};

const IconComponent = ({ isOpen , toggle }) => {
  return (
    <>
      <Box
        display={{ base: isOpen ? "block" : "none", md: "block" }}
        flexBasis={{ base: "100%", md: "auto" }}
      >
        <Stack
          spacing={8}
          align="flex-end"
          justify={["flex-end", "space-around", "flex-end", "center"]}
          direction={["column", "row", "row", "row"]}
          pt={[4, 4, 0, 0]}
        >
          <Box display={{ base: "block", md: "none" }} onClick={toggle}>
            {isOpen ? <AiOutlineClose size={40} /> : <BiMenu size={40} />}
          </Box>
        </Stack>
      </Box>
    </>
  );
};

const NavBarContainer = ({ children, ...props }) => {
  return (
    <Flex
      as="nav"
      align="center"
      justify="space-around"
      wrap="wrap"
      w="100vw"
      p="4%"
      {...props}
      border="1px solid red"
    >
      {children}
    </Flex>
  );
};

export default Navbar;
