import {
  Image,
  Box,
  Breadcrumb,
  BreadcrumbItem,
  BreadcrumbLink,
  Text,
  Stack,
} from "@chakra-ui/react";
// icons
import { ChevronRightIcon } from "@chakra-ui/icons";
import NatureImg from "../../Assets/temp-img/7.jpg";
import { useNavigate } from "react-router-dom";

// fonts
import "@fontsource/montserrat";
import "@fontsource/poppins";

const BannerComponent = () => {
  return (
    <>
      <Box>
        <Image
          zIndex={"1"}
          src={
            "https://www.notion.so/image/https%3A%2F%2Fwww.notion.so%2Fimages%2Fpage-cover%2Fgradients_11.jpg?width=400&userId=2659c72c-8d1e-4a2c-b5ac-eded7d61b9dc&cache=v2"
          }
          objectFit={"cover"}
          width="100vw"
          height={"sm"}
          alt="banner"
        />
      </Box>
      <MessageComponent />
    </>
  );
};
export default BannerComponent;

const MessageComponent = () => {
  const Navigate = useNavigate();
  return (
    <>
      <Box
        width={{
          "2xl": "14rem",
          xl: "12rem",
          lg: "10rem",
          md: "10pc",
          base: "8pc",
        }}
        height={{
          "2xl": "14rem",
          xl: "12rem",
          lg: "10rem",
          md: "10pc",
          base: "8pc",
        }}
        bg={"white"}
        border={
          {
            "2xl": "1px solid blue",
            xl: "1px solid pink",
            lg: "1px solid orange",
            md: "1px solid red",
          }
        }
        borderRadius="50%"
        pos={"relative"}
        left={{ "2xl": "45vw", lg: "50%" }}
        bottom={{ "2xl": "12rem" }}
        zIndex={"5"}
      >
        <Stack
          // border="1px solid grey"
          pos={"relative"}
          top="5rem"
          display={"flex"}
          alignItems="center"
          justifyContent="center"
          flexDir="column"
          gap=".3rem"
        >
          <Text
            textAlign="center"
            fontFamily={"montserrat"}
            fontWeight={"bold"}
            fontSize={{ "2xl": "3xl", xl: "lg", lg: "xl" }}
          >
            Products
          </Text>
          <Breadcrumb
            textAlign={"center"}
            spacing="15px"
            separator={
              <ChevronRightIcon
                color="gray.500"
                boxSize={{ xl: "5", lg: "4" }}
              />
            }
          >
            <BreadcrumbItem>
              <BreadcrumbLink onClick={() => Navigate("/")}>
                <Text
                  fontFamily={"montserrat"}
                  fontWeight={"light"}
                  fontSize={{ "2xl": "lg", xl: "lg", lg: "md" }}
                  _hover={{ color: "orange.500", cursor: "pointer" }}
                >
                  Home
                </Text>
              </BreadcrumbLink>
            </BreadcrumbItem>

            <BreadcrumbItem>
              <BreadcrumbLink onClick={() => Navigate("/products")}>
                <Text
                  fontFamily={"montserrat"}
                  fontWeight={"light"}
                  fontSize={{ "2xl": "lg", xl: "lg", lg: "md" }}
                  _hover={{ color: "orange.500", cursor: "pointer" }}
                >
                  Products
                </Text>
              </BreadcrumbLink>
            </BreadcrumbItem>
          </Breadcrumb>
        </Stack>
      </Box>
    </>
  );
};
