import { Pagination, Navigation, Keyboard, Autoplay } from "swiper";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

import "./carousel.css";

// images

import Footsole from "../Image/temp-img/foot-sole.png";
import SafetyJacket from "../Image/temp-img/safety";
import Tshirt from "../Image/temp-img/t-shirt.png";
import Tape from "../Image/temp-img/tape.png";
import YellowTape from "../Image/temp-img/yellow-tape.png";

import { ChakraProvider, Image } from "@chakra-ui/react";

const Herocarousel = () => {
  // const useStyles = {
  //   swiperContainer: {
  //     paddingBottom: "3rem",
  //     "& .swiper-pagination-bullet": {
  //       background: "blue",
  //     },
  //     "& .swiper-button-next:after": {
  //       fontSize: "2rem !important",
  //     },
  //     "& .swiper-button-prev:after": {
  //       fontSize: "2rem !important",
  //     },
  //   },
  // };
  return (
    <>
      <ChakraProvider>
        <Swiper
          style={{
            "--swiper-navigation-color": "grey",
            "--swiper-pagination-color": "pink",
            SwiperContainer: {
              paddingBottom: "3rem",
            },
          }}
          slidesPerView={1}
          spaceBetween={30}
          loop={true}
          pagination={{
            clickable: true,
          }}
          autoplay={{
            delay: 2500,
            disableOnInteraction: false,
          }}
          keyboard={{
            enabled: true,
          }}
          navigation={true}
          modules={[Pagination, Navigation, Keyboard, Autoplay]}
          className="swiperContainer"
        >
          <SwiperSlide>
            <Image src={Footsole} alt="footsole" />
          </SwiperSlide>
          <SwiperSlide>
            <Image src={SafetyJacket} alt="safetyjacket" />
          </SwiperSlide>
          <SwiperSlide>
            <Image src={Tshirt} alt="tshirt" />
          </SwiperSlide>
          <SwiperSlide>
            <Image src={Tape} alt="tape" />
          </SwiperSlide>
          <SwiperSlide>
            <Image src={YellowTape} alt="YellowTape" />
          </SwiperSlide>
        </Swiper>
      </ChakraProvider>
    </>
  );
};

export default Herocarousel;
