import {
  Box,
  Text,
  Flex,
  Image,
  Button,
  Tag,
  useToast,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalFooter,
  ModalBody,
  useDisclosure,
  Collapse,
  Stack,
} from "@chakra-ui/react";

// star component
import ReactStars from "react-rating-stars-component";

// context Api
import { CartState } from "./Context/context";
import axios from "axios";

// icons
import DeleteOutlineIcon from "@mui/icons-material/DeleteOutline";
import { CloseIcon } from "@chakra-ui/icons";
import RemoveIcon from "@mui/icons-material/Remove";
import AddIcon from "@mui/icons-material/Add";
import { useState } from "react";
import { useEffect } from "react";

const CartPageData = () => {
  return (
    <>
      <CartPageInitialComponent />
      <Box>
        <CartPageSecondryComponent />
      </Box>
    </>
  );
};

export default CartPageData;

const CartPageInitialComponent = () => {
  return (
    <>
      <Box
        display={"flex"}
        alignItems="center"
        justifyContent={"center"}
        padding={"1vmax"}
      >
        <Text fontSize={"5xl"} fontWeight={"bold"}>
          Cart Items
        </Text>
      </Box>
    </>
  );
};

const CartPageSecondryComponent = () => {
  return (
    <>
      <Flex w={"100vw"} gap={"2%"} px={"2%"} py={"2%"}>
        <CartItems />
        <CartPaymentPage />
      </Flex>
    </>
  );
};

const CartItems = ({ prod }) => {
  // console.log(prod.name);
  // btn increamnet decreamnet
  const [BtnValue, setBtnValue] = useState(1);
  const {
    state: { cart },
    dispatch,
  } = CartState();
  const { isOpen, onOpen, onClose } = useDisclosure();
  const toast = useToast();
  return (
    <Flex
      boxShadow="inset 0 0 0 1px #e9e9eb"
      rounded={"md"}
      padding={"1rem"}
      w={"60%"}
      flexWrap={"wrap"}
      flexDirection={"column"}
      gap={"1rem"}
    >
      
    </Flex>
  );
};

const CartPaymentPage = () => {
  const {
    state: { cart },
    // dispatch,
  } = CartState();
  const [Total, setTotal] = useState();
  const [Discount, setDiscount] = useState();

  // payment
  const handlePayment = async () => {
    try {
      const orderUrl = "http://localhost:8080/api/payment/orders";
      const { data } = await axios.post(orderUrl, { amount: Total - Discount });
      console.log(data);
      initPayment(data.data);
    } catch (error) {
      console.log(error);
    }
  };

  const initPayment = (data) => {
    const options = {
      key: "YOUR_RAZORPAY_KEY",
      amount: data.amount,
      currency: data.currency,
      name: book.name,
      description: "Test Transaction",
      image: book.img,
      order_id: data.id,
      handler: async (response) => {
        try {
          const verifyUrl = "http://localhost:8080/api/payment/verify";
          const { data } = await axios.post(verifyUrl, response);
          console.log(data);
        } catch (error) {
          console.log(error);
        }
      },
      theme: {
        color: "#3399cc",
      },
    };
    const rzp1 = new window.Razorpay(options);
    rzp1.open();
  };

  useEffect(() => {
    return () => {
      setTotal(cart?.reduce((acc, curr) => acc + Number(curr.price), 0));
      setDiscount(
        cart?.reduce(
          (acc, curr) =>
            acc +
            Math.round((Number(curr.discount) / Number(curr.price)) * 100),
          0
        )
      );
    };
  });

  return (
    <>
      <Box
        w={"20vw"}
        height={"60vh"}
        position={"fixed"}
        right={"10%"}
        boxShadow="inset 0 0 0 1px #e9e9eb"
        rounded={"md"}
        padding={"1vmax"}
      >
        <Flex flexDirection={"column"} gap={"3rem"}>
          {/* // subtotal */}
          <Box
            display={"flex"}
            justifyContent={"space-between"}
            alignItems={"center"}
            w={"100%"}
            px={"4%"}
          >
            <Text fontSize={"lg"}>Sub Total :</Text>
            <Text fontWeight={"bold"} fontSize={"lg"}>
              {cart.length} Items
            </Text>
          </Box>
          {/* Total Mrp */}
          <Box
            display={"flex"}
            justifyContent={"space-between"}
            alignItems={"center"}
            w={"100%"}
            px={"4%"}
          >
            <Text fontSize={"lg"}>Total MRP</Text>
            <Text fontSize={"lg"} color={"orange.500"}>
              $ {Total}
            </Text>
          </Box>
          {/* discount on mrp */}
          <Box
            display={"flex"}
            justifyContent={"space-between"}
            alignItems={"center"}
            w={"100%"}
            px={"4%"}
          >
            <Text fontSize={"lg"}>Discount</Text>
            <Text fontSize={"lg"} color={"orange.500"}>
              ${Discount}
            </Text>
          </Box>
          {/* convinience fee */}
          <Box
            display={"flex"}
            justifyContent={"space-between"}
            alignItems={"center"}
            w={"100%"}
            px={"4%"}
          >
            <Text fontSize={"lg"}>Convinience Fee</Text>
            <Text fontSize={"lg"} color={"orange.500"}>
              Free
            </Text>
          </Box>
          {/* total amount */}
          <Box
            display={"flex"}
            justifyContent={"space-between"}
            alignItems={"center"}
            w={"100%"}
            px={"4%"}
          >
            <Text fontSize={"lg"}>Total Amount</Text>
            <Text fontSize={"lg"} color={"orange.500"}>
              ${Total - Discount}
            </Text>
          </Box>
          {/* buttons */}
          <Box px={"4%"}>
            <Button
              w={"100%"}
              bg={"orange.500"}
              color={"white"}
              fontWeight={"light"}
            >
              Place Order
            </Button>
          </Box>
        </Flex>
      </Box>
    </>
  );
};
