import React, { useState, useEfect } from "react";
import {
  Box,
  Heading,
  Link,
  Image,
  Grid,
  Text,
  Divider,
  HStack,
  Tag,
  Wrap,
  WrapItem,
  SpaceProps,
  useColorModeValue,
  Container,
  VStack,
  GridItem,
} from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";

const BlogSection = () => {
  // const [posts, setposts] = useState([]);
  //   console.log(HashnodeData.posts)
  const Item = HashnodeData.posts;
  let DisplaythreePost = [];
  if (Item.length > 0) {
    for (let index = 0; index < 3; index++) {
      DisplaythreePost = [...DisplaythreePost, Item[index]];
    }
  }

  return (
    <>
      <Grid
        mx={{ xl: "10%", lg: "10%", md: "10%" }}
        my={{ xl: "5%", lg: "5%", md: "5%" }}
        p={{ xl: "2%", lg: "2%",md:'2.5%'}}
        templateColumns={[
          "repeat(1, 1fr)",
          "repeat(2, 1fr)",
          "repeat(3, 1fr)",
          "repeat(3, 1fr)",
        ]}
        gap={5}
      >
        {DisplaythreePost.map((data) => {
          return <SingleBlog data={data} />;
        })}
      </Grid>
    </>
  );
};

export default BlogSection;

const SingleBlog = ({ data }) => {
  return (
    <>
      <Box
        px={"2%"}
        py={"2%"}
        borderRadius="xl"
        minH="100%"
        // cursor="pointer"
        border="1px solid #E7EBF0"
        bg="white"
        // boxShadow=" 0 15px 5px 0 rgba(29, 78, 216, 0.2)"
        p="5"
        flexWrap="wrap"
        m="2"
      >
        {/* <Box> */}
        <Box
          borderRadius="lg"
          overflow="hidden"
          display="flex"
          px={{ xl: "2%", lg: "2%", md: "1.5%" }}
          py={{ xl: "2%", lg: "2%", md: "1.5%" }}
        >
          {/* <Link textDecoration="none" _hover={{ textDecoration: "none" }}> */}
          <Image
            transform="scale(1.0)"
            src={data.coverImage}
            alt={data.slug}
            objectFit="cover"
            h="auto"
            w="100%"
            borderRadius={"md"}
            transition="0.3s ease-in-out"
            _hover={{
              transform: "scale(1.08)",
            }}
          />
          {/* </Link> */}
        </Box>
        <Text as="h1" fontWeight="bold" fontSize="xl">
          {data.title}
        </Text>
        <Text as="p" mt="2" noOfLines={4}>
          {data.brief}
        </Text>
        {/* </Box> */}
      </Box>
    </>
  );
};
