import React from "react";
import { Box, Flex } from "@chakra-ui/react";
import FilterCard from "./FilterComponent";
import CartItems from "./CartItems";

const FullComponent = () => {
  return (
    <>
      <Flex
        pos={"absolute"}
        px="1rem"
        py="1rem"
        gap={"1rem"}
        border={'1px solid'}
        top={{ xl: "80%", lg: "80%", md: "80%", base: "90%" }}
        w="100%"
      >
        <FilterPortion />
        <CartItemsPortion />
      </Flex>
    </>
  );
};

export default FullComponent;

const FilterPortion = ({ children, ...props }) => {
  return (
    <>
      <Flex
        {...props}
        border="1px solid lightgrey"
        borderRadius={"md"}
        boxShadow={"base"}
        padding=".5rem"
        minW="18vw"
        maxH={"80vh"}
      >
        <FilterCard />
      </Flex>
    </>
  );
};

const CartItemsPortion = ({ children, ...props }) => {
  return (
    <>
      <Box
        {...props}
        boxShadow="base"
        border="1px solid lightgrey"
        borderRadius={"md"}
        w={"80vw"}
        pos="relative"
      >
        <Box>
          <CartItems />
        </Box>
      </Box>
    </>
  );
};
