import React from "react";
import { Box, Heading, Image } from "@chakra-ui/react";
import { faker } from "@faker-js/faker";

function TrendingProduct(props) {
  // eslint-disable-next-line

  return (
    <>
      <ProdContainer {...props}>
        <ProdContainerHeader />
        <ProdContainerItem />
      </ProdContainer>
    </>
  );
}
const ProdContainerHeader = () => {
  return (
    <>
      <Box
        // px="1.5rem"
        py="1.5%"
        mx={{ base: "10%" }}
        display="flex"
        justifyContent={["center", "flex-start", "flex-start", "flex-start"]}
      >
        <Heading
          fontSize={["1.5rem", "1.75rem"]}
          fontWeight={["500", "600"]}
          as="h2"
          color="#2c2c2c"
        >
          <Box display={["inline-block"]}>Hot Products</Box>
          <Box
            borderBottom="2px solid #e97730"
            py={["1"]}
            pos={["relative"]}
            w={["70%", "60%"]}
            left={["4", "4"]}
            right={["4", "4"]}
          ></Box>
        </Heading>
      </Box>
    </>
  );
};

const ProdContainerItem = () => {
  const img = faker.image.transport() ;
  console.log(img);

  return (
    <>
      <Box>
        <Image src={img} />
      </Box>
    </>
  );
};
const ProdContainer = ({ children, ...props }) => {
  return <Box>{children}</Box>;
};

export default TrendingProduct;
