import React, { useState } from "react";
import {
  Box,
  Flex,
  Text,
  Stack,
  useColorMode,
  Image,
  HStack,
  VStack,
} from "@chakra-ui/react";
// icons
// import { NavHashLink } from "react-router-hash-link";
import Bag from "../Image/Bag.png";
import User from "../Image/User.png";
import Menu from "../Image/Menu.png";
import Close from "../Image/Close.png";

function Navbar(props) {
  // eslint-disable-next-line
  const { colorMode, toggleColorMode } = useColorMode();
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => setIsOpen(!isOpen);
  return (
    <NavBarContainer {...props}>
      <a href="/">
        <Text as="h1" fontWeight="500" fontSize="2xl">
          Logo
        </Text>
      </a>
      <HStack border="1px solid" w={{ md: "70%", xl: "70%" }}>
      <MenuLinks />
      </HStack>
    </NavBarContainer>
  );
}

const MenuLinks = ({ isOpen }) => {
  return (
    // <Box>
    <HStack spacing={8} pt={[4, 4, 1, 1]} p>
      <a href="/">
        <Text display="block" fontWeight="500">
          Home
        </Text>
      </a>

      <a href="/#Products">
        <Text display="block" fontWeight="500">
          Products
        </Text>
      </a>

      <a href="/#Training">
        <Text display="block" fontWeight="500">
          {/* Training and Consultation */}
          Training
        </Text>
      </a>

      <a href="/#5s-Warm-Up">
        <Text display="block" fontWeight="500">
          5s Warm Up
        </Text>
      </a>
    </HStack>
    // </Box>
  );
};

const IconComponent = () => {
  return (
    <>
      <Box
      // display={{ base: isOpen ? "block" : "none", md: "block" }}
      // flexBasis={{ base: "100%", md: "auto" }}
      >
        <Stack
        //   spacing={8}
        //  V
        //   pt={[4, 4, 0, 0]}
        >
          <Image src={Bag} height="30px" width="auto" />
          <Image src={User} height="30px" width="auto" />
        </Stack>
      </Box>
    </>
  );
};

const NavBarContainer = ({ children, ...props }) => {
  return (
    <Flex
      as="nav"
      align="center"
      justify="space-between"
      wrap="wrap"
      w="100vw"
      p="1.25%"
      {...props}
      border="1px solid red"
    >
      {children}
    </Flex>
  );
};

export default Navbar;
