import React from "react";
import {
  Box,
  Text,
  Stack,
  useColorMode,
  Image,
  HStack,
  Button,
  useDisclosure,
  // Divider,

  // MenuButton,
  // FormLabel,
  Select,
  // MenuItem,
  // MenuList,
} from "@chakra-ui/react";

// icons
import { BiMenu } from "react-icons/bi";
// import { AiOutlineClose } from "react-icons/ai";
import Bag from "../Image/Bag.png";
import User from "../Image/User.png";
import { ChevronRightIcon } from "@chakra-ui/icons";

// fonts
import "@fontsource/montserrat";
import "@fontsource/poppins";

// drawer
import {
  Drawer,
  DrawerBody,
  DrawerFooter,
  DrawerHeader,
  DrawerOverlay,
  DrawerContent,
  DrawerCloseButton,
} from "@chakra-ui/react";

// import { ColorModeSwitcher } from "../ColorModeSwitcher";
// import { NavHashLink } from "react-router-hash-link";

// https://github.com/dimitrisraptis96/chakra-ui-navbar/blob/main/src/components/Header.js
// https://raptis.wtf/freebies/color-pairs

function Navbar(props) {
  // eslint-disable-next-line
  const { colorMode, toggleColorMode } = useColorMode();
  const [SetOpen, setIsOpen] = React.useState(false);

  const toggle = () => setIsOpen(!SetOpen);

  return (
    <>
      <NavBarContainer
        {...props}
        bg={colorMode === "light" ? "bodyBackground" : ""}
      >
        <LogoComponent />
        <MenuLinks SetOpen={SetOpen} />
        <MenuToggle toggle={toggle} SetOpen={SetOpen} />
      </NavBarContainer>
    </>
  );
}

const LogoComponent = () => {
  return (
    <Box display="flex" justifyContent="center">
      <a href="/">
        <Text as="h2" fontWeight="500" fontSize="2xl">
          Logo
        </Text>
      </a>
    </Box>
  );
};

const MenuToggle = () => {
  const { isOpen, onOpen, onClose } = useDisclosure();
  const btnRef = React.useRef();
  return (
    <>
      <Box
        display={["flex"]}
        alignItems={["center"]}
        justifyContent={{ base: "flex-end" }}
      >
        <IconComponent />
        <Box display={{ base: "block", md: "none" }}>
          {/* <Box display={{ base: "block", md: "none" }} onClick={toggle}>
          {isOpen ? <AiOutlineClose size={36} /> : <BiMenu size={36} />}
        </Box> */}
          <Box pl={[".35rem"]} ref={btnRef} onClick={onOpen}>
            <Button variant="outline">
              <BiMenu size={30} />
            </Button>
          </Box>

          <Drawer
            display={{ md: "none", lg: "none", xl: "none" }}
            isOpen={isOpen}
            placement="right"
            onClose={onClose}
            finalFocusRef={btnRef}
            h={["100vh"]}
          >
            <DrawerOverlay />

            <DrawerContent
              display={["flex"]}
              flexDirection={["column"]}
              gap={["1rem"]}
              justifyContent={["space-between"]}
            >
              <DrawerCloseButton />

              <DrawerHeader
                h={["25%"]}
                fontWeight="500"
                display={["flex "]}
                flexDirection={["column"]}
                // alignItems={["center"]}
                fontSize="1.25rem"
                px={["1.5rem"]}
                py={["2rem"]}
              >
                Default Welcome Msg!{" "}
              </DrawerHeader>

              <DrawerBody h={["50%"]}>
                <MenuLinks isOpen={isOpen} onOpen={onOpen} onClose={onClose} />
              </DrawerBody>

              <DrawerFooter
                display={["flex"]}
                gap={["1rem"]}
                alignSelf={["flex-start"]}
                width="100%"
                px={["1rem"]}
                py={["1rem"]}
                h={["25%"]}
              >
                <DrawerFooterButton />
              </DrawerFooter>
            </DrawerContent>
          </Drawer>
        </Box>
      </Box>
    </>
  );
};

const DrawerFooterButton = () => {
  return (
    <>
      <Select
        width={["50%"]}
        placeholder="$ USD"
        // border="1px solid"
        border="none"
        textAlign="center"
        bg="none"

        // fontWeight="semibold"
      >
        <option>â‚¹ Rupee</option>
      </Select>{" "}
      <Select
        width={["50%"]}
        placeholder="ENGLISH"
        // border="1px solid"
        border="none"
        textAlign="center"
        bg="none"
      >
        <option>GUJARATI</option>
      </Select>
    </>
  );
};

const MenuLinks = ({ isOpen, onOpen, onClose }) => {
  return (
    <Box
      display={{ base: isOpen ? "block" : "none", md: "block" }}
      // flexBasis={{ base: "100%", md: "auto" }}
      // display={{}}
      flexDirection={["column"]}
    >
      <Stack
        fontWeight="500"
        spacing={8}
        gap={["1.5rem"]}
        align={["flex-between", "center", "center", "center"]}
        justify={["center", "space-around", "space-around", "space-around"]}
        direction={["column", "row", "row", "row"]}
        // pt={[4, 4, 0, 0]}
        // py={[".5rem"]}
      >
        <Box display={["flex"]} alignItems={["center"]}>
          <a href="/">
            <Text display="block" fontWeight="500" textAlign={["center"]}>
              Home
            </Text>
          </a>
          <Box display={{ md: "none" }}>
            <ChevronRightIcon />
          </Box>
        </Box>
        <Box display={["flex"]} alignItems={["center"]}>
          <a href="/#Products">
            <Text display="block" fontWeight="500" textAlign={["center"]}>
              Products
            </Text>
          </a>
          <Box display={{ md: "none" }}>
            <ChevronRightIcon />
          </Box>
        </Box>

        <Box display={["flex"]} alignItems={["center"]}>
          <a href="/#Training">
            <Text display="block" fontWeight="500" textAlign={["center"]}>
              {/* Training and Consultation */}
              Academy
            </Text>
          </a>
          <Box display={{ md: "none" }}>
            <ChevronRightIcon />
          </Box>
        </Box>

        <Box display={["flex"]} alignItems={["center"]}>
          <a href="/#5s-Warm-Up">
            <Text display="block" fontWeight="500" textAlign={["center"]}>
              Resources
            </Text>
          </a>
          <Box display={{ md: "none" }}>
            <ChevronRightIcon />
          </Box>
        </Box>

        {/* <ColorModeSwitcher /> */}
      </Stack>
    </Box>
  );
};

const IconComponent = () => {
  return (
    <>
      <HStack
        gap={["null", ".15rem", ".15rem", ".15rem"]}
        justifyContent={["center", "center", "center", "center"]}
      >
        <Image src={Bag} height="24px" width="auto" />
        <Image src={User} height="24px" width="auto" />
      </HStack>
    </>
  );
};

const NavBarContainer = ({ children, ...props }) => {
  return (
    <Box
      display={["flex", "flex", "grid", "grid"]}
      as="nav"
      _hover={{ color: "orange.500" }}
      // for small screen only
      justifyContent={["space-between", "space-between", "space-between"]}
      alignItems={["center", "center", "center", "center"]}
      w="100vw"
      columnGap={{ md: "2rem", lg: "1.5rem", xl: ".5rem" }}
      px={["3%", "1.5%", "1.6%", "1.8%"]}
      py={["2%", "1.5%", "1.6%", "1.8%"]}
      gridTemplateColumns={["null", "null", ".5fr 2fr .5fr", ".5fr 2fr .5fr"]}
      fontFamily="montserrat"
      {...props}
      boxShadow="rgba(0, 0, 0, 0.02) 0px 1px 3px 0px, rgba(27, 31, 35, 0.15) 0px 0px 0px 1px"
    >
      {children}
    </Box>
  );
};

export default Navbar;
