import React, { useState, useEfect } from "react";
import {
  Box,
  Heading,
  Link,
  Image,
  Grid,
  Text,
  Divider,
  HStack,
  Tag,
  Wrap,
  WrapItem,
  SpaceProps,
  useColorModeValue,
  Container,
  VStack,
  GridItem,
} from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";

const BlogSection = () => {
  // const [posts, setposts] = useState([]);
  //   console.log(HashnodeData.posts)
  const Item = HashnodeData.posts;
  let DisplaythreePost = [];
  if (Item.length > 0) {
    for (let index = 0; index < 3; index++) {
      DisplaythreePost = [...DisplaythreePost, Item[index]];
    }
  }

  return (
    <>
      <Grid
        templateColumns={{
          xl: "200px 200px ",
          lg: "200px 200px ",
          md: "200px 200px ",
        }}
        columnGap="2rem"
        mx={{ xl: "10%", lg: "10%", md: "10%" }}
        my={{ xl: "5%", lg: "5%", md: "5%" }}
      >
        {DisplaythreePost.map((data) => {
          console.log(data);
          return (
            <Grid
              templateRows="1fr"
              placeContent="center"
              border="1px solid #E7EBF0"
              _hover={{
                boxShadow:
                  "rgba(60, 64, 67, 0.3) 0px 1px 2px 0px, rgba(60, 64, 67, 0.15) 0px 2px 6px 2px;",
                transition: ".5s ease-in-out",
              }}
              borderRadius=".5rem"
              bg="white"
            >
              <SingleBlog data={data} />
            </Grid>
          );
        })}
      </Grid>
    </>
  );
};

export default BlogSection;

const SingleBlog = ({ data }) => {
  return (
    <>
      <Box borderRadius="lg" overflow="hidden" display="flex" px={"2%"}>
        {/* <Link textDecoration="none" _hover={{ textDecoration: "none" }}> */}
        <Image
          transform="scale(1.0)"
          src={data.coverImage}
          alt={data.slug}
          objectFit="cover"
          h="200px"
          w="320px"
          borderRadius={"md"}
          transition="0.3s ease-in-out"
          _hover={{
            transform: "scale(1.05)",
          }}
        />
        {/* </Link> */}
      </Box>
      <Heading fontSize="xl" marginTop="2" px={"2%"}>
        <Link textDecoration="none" _hover={{ textDecoration: "none" }}>
          {data.title}
        </Link>
      </Heading>
      <Text as="p" fontSize="md" marginTop="2" pt="2%" pb={"5%"} px={"2%"}>
        {data.brief}
      </Text>
    </>
  );
};
