import React from "react";
import { Box, Flex } from "@chakra-ui/react";
import FilterComponent from "./FilterComponent";
import CartItems from "./CartItems";

const FullComponent = () => {
  return (
    <>
      <Flex
        pos={"absolute"}
        px="3"
        py="4"
        gap={'2rem'}
        // bottom={{ xl: "8rem", lg: "7rem", md: "6rem", base: "4rem" }}
        top={{ xl: "80%", lg: "80%",md:'80%',base:'90%' }}
        w="100%"
      >
        <FilterPortion />
        <CartItemsPortion />
      </Flex>
    </>
  );
};

export default FullComponent;

const FilterPortion = ({ children, ...props }) => {
  return (
    <>
      <Flex
        {...props}
        bg="lightcoral"
        borderRadius={"md"}
        border={"1px solid"}
        w="20vw"
        height={'25vmax'}
        position="fixed" 
        top="40%"
      >
        <Box>
          <FilterComponent />
        </Box>
      </Flex>
    </>
  );
};

const CartItemsPortion = ({ children, ...props }) => {
  return (
    <>
      <Flex
        {...props}
        bg="pink"
        borderRadius={"md"}
        border={"1px solid"}
        w={"80vw"}
        minH={"90vh"}
      >
        <Box>
          <CartItems />
        </Box>
      </Flex>
    </>
  );
};
