import React from "react";
import {
  Box,
  Heading,
  Text,
  Stack,
  Avatar,
  Image,
  Tag,
} from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";

const BlogSection = () => {
  const Item = HashnodeData.posts;
  let DisplaythreePost = [];
  if (Item.length > 0) {
    for (let index = 0; index < 3; index++) {
      DisplaythreePost = [...DisplaythreePost, Item[index]];
    }
  }

  return (
    <>
      <Box
        mx={{ xl: "10%", lg: "10%", md: "10%" }}
        mb={["1rem"]}
        py={{ xl: "4%", lg: "4%" }}
        pt={{ sm: "1rem", md: "1rem" }}
        display={{ sm: "flex", md: "grid" }}
        // border="1px solid"
        // margin="0 auto"
        pl={{ xl: "1rem", lg: "1rem" }}
        pr={{ xl: "1rem", lg: "1rem" }}
        flexDirection={{ sm: "column" }}
        gridTemplateColumns={{
          md: "repeat(2, 1fr)",
          lg: "repeat(3, 1fr)",
          xl: "repeat(3, 1fr)",
        }}
        // justifyContent={'space-between'}
        padding={["1rem", "1rem"]}
        // mx={{ xl: "10%", lg: "10%", md: "10%" }}
        gap={10}
      >
        {DisplaythreePost.map((data, index) => (
          <Stack
            key={data.id}
            border="1px solid #E7EBF0"
            rounded={"md"}
            p={4}
            overflow={"hidden"}
          >
            <Box
              h={"220px"}
              bg={"gray.100"}
              // mt={-6}
              // mx={-6}
              mb={6}
              // pos={"relative"}
            >
              <Image
                borderRadius="md"
                src={data.coverImage}
                layout={"cover"}
                h="100%"
                w="100%"
                key={data.slug}
                transition="0.3s ease-in-out"
              />
            </Box>
            <Stack>
              <Text
                color={"green.500"}
                textTransform={"uppercase"}
                fontWeight={800}
                fontSize={"sm"}
                letterSpacing={1.1}
              >
                <Tag>{data.tags}</Tag>
              </Text>
              <Heading
                fontSize={"2xl"}
                transition="0.3s ease-in-out"
                _hover={{
                  color: "orange",
                  cursor: "pointer",
                }}
              >
                {data.title}
              </Heading>
              <Text color={"gray.500"} noOfLines={4}>
                {data.brief}
              </Text>
            </Stack>
            <Stack
              mt={6}
              direction={"row"}
              spacing={4}
              // border="1px solid"
              align="center"
            >
              <Avatar name="Vaishali Parekh" alt={"Author"} />
              <Stack direction={"column"} spacing={0} fontSize={"sm"}>
                <Text fontWeight={600}>Vaishali Parekh</Text>
                <Text color={"gray.500"}>
                  {data.dateAdded} Â· {data.readTime}min read
                </Text>
              </Stack>
            </Stack>
          </Stack>
        ))}
        {/* </Box> */}
      </Box>
    </>
  );
};

export default BlogSection;
