import React, { useState, useEfect } from "react";
import {
  Box,
  Heading,
  Link,
  Image,
  Text,
  Divider,
  HStack,
  Tag,
  Wrap,
  WrapItem,
  SpaceProps,
  useColorModeValue,
  Container,
  VStack,
} from "@chakra-ui/react";
import HashnodeData from "../Data/hashnode.json";

const BlogSection = () => {
  // const [posts, setposts] = useState([]);
  //   console.log(HashnodeData.posts)
  const Item = HashnodeData.posts;
  let DisplaythreePost = [];
  if (Item.length > 0) {
    for (let index = 0; index < 3; index++) {
      DisplaythreePost = [...DisplaythreePost, Item[index]];
    }
  }

  return (
    <>
      {DisplaythreePost.map((data) => {
        console.log(data);
        return (
          <Box w="100%" key={data.id} mx={{ xl: "10%", lg: "10%", md: "10%" }}>
            <Box border="1px solid" display={'grid'} gridTemplateColumns>
              <Box borderRadius="lg" overflow="hidden">
                <Link textDecoration="none" _hover={{ textDecoration: "none" }}>
                  <Image
                    transform="scale(1.0)"
                    src={data.coverImage}
                    alt={data.slug}
                    objectFit="cover"
                    width="25%"
                    transition="0.3s ease-in-out"
                    _hover={{
                      transform: "scale(1.05)",
                    }}
                  />
                </Link>
              </Box>
              {/* <BlogTags tags={["Engineering", "Product"]} marginTop="3" /> */}
              <Heading fontSize="xl" marginTop="2">
                <Link textDecoration="none" _hover={{ textDecoration: "none" }}>
                  {data.title}
                </Link>
              </Heading>
              <Text as="p" fontSize="md" marginTop="2">
                {data.brief}
              </Text>
              {/* <BlogAuthor name="John Doe" date={new Date("2021-04-06T19:01:27Z")} /> */}
            </Box>
          </Box>
        );
      })}
    </>
  );
};

export default BlogSection;
