// import "./index.css";
// import "./App.css";
import { Box, Image, Button } from "@chakra-ui/react";
import products from "../Data/data.json";

export default function App() {
  return (
    <Box className={"container"}>
      <Box
        display={["grid",]}
        flexDirection={["column", "row", "row", "row"]}
        justifyContent={["center", "space-between", "space-between"]}
        gap={["1rem"]}
      >
        {products.map((product, i) => (
          <Product {...product} key={i} />
        ))}
      </Box>

      <div
        id="snipcart"
        data-api-key="NWMwZWNkZGMtZjU2ZS00YzM3LWFlZjYtMmM5Zjk0MWViZDcxNjM3Njg0OTY0ODg5NTk4MTM3"
        hidden
      ></div>
    </Box>
  );
}

// import "./index.css";

function Product(props) {
  const { id, imageUrl, name, description, price } = props;

  return (
    <Box key={id} border="1px solid lightgrey" padding={[".5rem"]} borderRadius=".5rem" display="flex" justifyContent={["flex-start"]} flexDirection={["column"]}  >
      <Image
        src={imageUrl}
        alt={`Image of ${name}`}
        className={"image-product"}
        width={["60%", "70%", "80%", "90%"]}
        borderRadius=".25rem"
      />
      <h3>{name}</h3>
      <p>{description}</p>
      <span>${price}</span>
      <div>
        <Button
          className="snipcart-add-item"
          data-item-id={id}
          data-item-image={imageUrl}
          data-item-name={name}
          data-item-url="/"
          data-item-price={price}
        >
          Add to Cart
        </Button>
      </div>
    </Box>
  );
}
