import React from "react";
import { Box, Flex, Text, Button, Stack, Image } from "@chakra-ui/react";
// import Bag from "../Image/Bag.png";
// import User from "../Image/User.png";
import Menu from "../Image/Menu.png";
import Close from "../Image/Close.png";

function Navbar(props) {
  // eslint-disable-next-line
  const [isOpen, setIsOpen] = React.useState(false);

  const toggle = () => setIsOpen(!isOpen);

  return (
    <NavBarContainer
      {...props}
    >
      <a href="/">
        <Text as="h2" fontWeight="500" fontSize="3xl">
          Logo
        </Text>
      </a>

      <MenuToggle toggle={toggle} isOpen={isOpen} />
      <MenuLinks isOpen={isOpen} />
    </NavBarContainer>
  );
}

const MenuToggle = ({ toggle, isOpen }) => {
  return (
    <Box display={{ base: "block", md: "none" }} onClick={toggle}>
      {isOpen ? (
        <Image src={Close} alt="Close-icon" boxSize="30px" />
      ) : (
        <Image src={Menu} alt="Menu-icon" boxSize="30px" />
      )}
    </Box>
  );
};

const MenuLinks = ({ isOpen }) => {
  return (
    <Box
      display={{ base: isOpen ? "block" : "none", md: "block" }}
      flexBasis={{ base: "100%", md: "auto" }}
    >
      <Stack
        spacing={8}
        align="center"
        justify={["flex-end", "space-between", "flex-end", "flex-end"]}
        direction={["column", "row", "row", "row"]}
        pt={[4, 4, 0, 0]}
      >
        <a href="/">
          <Text display="block" fontWeight="500">
            Home
          </Text>
        </a>

        <a href="/#Products">
          <Text display="block" fontWeight="500">
            Products
          </Text>
        </a>

        <a href="/#Training">
          <Text display="block" fontWeight="500">
            {/* Training and Consultation */}
            Training
          </Text>
        </a>

        <a href="/#5s-Warm-Up">
          <Text display="block" fontWeight="500">
            5s Warm Up
          </Text>
        </a>
      </Stack>
    </Box>
  );
};

const NavBarContainer = ({ children, ...props }) => {
  return (
    <Flex
      as="nav"
      align="center"
      justify="space-between"
      wrap="wrap"
      w="100vw"
      p="1%"
      border="1px solid red"

      {...props}
    >
      {children}
    </Flex>
  );
};

export default Navbar;
