import React from "react";
import { Box, Flex } from "@chakra-ui/react";
import FilterCard from "./FilterComponent";
import CartItems from "./CartItems";

// context Api
import { CartState } from "../Context/context";

const FullComponent = () => {
  return (
    <>
      <Flex
        pos={"absolute"}
        px="1rem"
        py="1rem"
        gap={"1rem"}
        top={{ xl: "80%", lg: "80%", md: "80%", base: "90%" }}
        w="100%"
      >
        <FilterPortion />
        <CartItemsPortion />
      </Flex>
    </>
  );
};

export default FullComponent;

const FilterPortion = ({ children, ...props }) => {
  return (
    <>
      <Flex
        {...props}
        border="1px solid lightgrey"
        borderRadius={"md"}
        boxShadow={"base"}
        padding=".5rem"
        maxH={"90vh"}
      >
        <FilterCard />
      </Flex>
    </>
  );
};

const CartItemsPortion = ({ children, ...props }) => {
  const {
    state: { products },
    productState: { sort, byStock, },
  } = CartState();

  const transformProducts = () => {
    let sortedProducts = products;

    if (sort) {
      sortedProducts = sortedProducts.sort((a, b) =>
        sort === "lowToHigh" ? a.price - b.price : b.price - a.price
      );
    }

    if (!byStock) {
      sortedProducts = sortedProducts.filter((prod) => prod.inStock);
    }
    if (state.dataFilter.filterByCategories.length !== 0)
      newData = newData.filter((product) =>
        state.dataFilter.filterByCategories.includes(product.category)
      );

    return sortedProducts;
  };
  return (
    <>
      <Box
        {...props}
        boxShadow="base"
        border="1px solid lightgrey"
        borderRadius={"md"}
        w={"80vw"}
        pos="relative"
      >
        <Box display={"flex"} flexWrap={"wrap"} justifyContent={"space-around"}>
          {transformProducts().map((prod) => (
            <CartItems prod={prod} key={prod.id} />
          ))}
        </Box>
      </Box>
    </>
  );
};
