import React, { useState } from "react";
import { Box, Flex, Text, Stack, useColorMode, Image } from "@chakra-ui/react";
// icons

// import { NavHashLink } from "react-router-hash-link";
import Bag from "../Image/Bag.png";
import User from "../Image/User.png";
import Menu from "../Image/Menu.png";
import Close from "../Image/Close.png";

function Navbar(props) {
  // eslint-disable-next-line
  const { colorMode, toggleColorMode } = useColorMode();
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => setIsOpen(!isOpen);
  return (
    <NavBarContainer {...props}>
      <a href="/">
        <Text as="h1" fontWeight="500" fontSize="2xl">
          Logo
        </Text>
      </a>

        <MenuToggle toggle={toggle} isOpen={isOpen} />
        <MenuLinks isOpen={isOpen} />
        <IconComponent />
    
    </NavBarContainer>
  );
}

const MenuToggle = ({ toggle, isOpen }) => {
  return (
    <Box display={{ base: "block", md: "none" }} onClick={toggle}>
      {isOpen ? (
        <Image src={Close} height="30px" width="auto" />
      ) : (
        <Image src={Menu} height="30px" width="auto" />
      )}
    </Box>
  );
};

const MenuLinks = ({ isOpen }) => {
  return (
    <Box
      display={{ base: isOpen ? "block" : "none", md: "block" }}
      flexBasis={{ base: "100%", md: "auto" }}
    >
      <Stack
        spacing={8}
        align="center"
        justify={["center", "space-around", "flex-end", "space-between"]}
        direction={["column", "row", "row", "row"]}
        pt={[4, 4, 1, 1]}
      >
        <a href="/">
          <Text display="block" fontWeight="500">
            Home
          </Text>
        </a>

        <a href="/#Products">
          <Text display="block" fontWeight="500">
            Products
          </Text>
        </a>

        <a href="/#Training">
          <Text display="block" fontWeight="500">
            {/* Training and Consultation */}
            Training
          </Text>
        </a>

        <a href="/#5s-Warm-Up">
          <Text display="block" fontWeight="500">
            5s Warm Up
          </Text>
        </a>
      </Stack>
    </Box>
  );
};

const IconComponent = () => {
  return (
    <>
      <Box
      // display={{ base: isOpen ? "block" : "none", md: "block" }}
      // flexBasis={{ base: "100%", md: "auto" }}
      >
        <Stack
          spacing={8}
          align="center"
          justify={["space-around", "space-around", "flex-end", "center"]}
          direction={["row", "row", "row", "row"]}
          //   pt={[4, 4, 0, 0]}
        >
          <Image src={Bag} height="30px" width="auto" />
          <Image src={User} height="30px" width="auto" />
        </Stack>
      </Box>
    </>
  );
};

const NavBarContainer = ({ children, ...props }) => {
  return (
    <Flex
      as="nav"
      align="center"
      justify="space-between"
      wrap="wrap"
      w="100vw"
      p="1.25%"
      {...props}
      border="1px solid red"
    >
      {children}
    </Flex>
  );
};

export default Navbar;
