import React from "react";
import {
  FormControl,
  // Menu,
  Text,
  Select,
  FormLabel,
  Flex,
  Box,
} from "@chakra-ui/react";

const FilterComponent = () => {
  return (
    <>
      <Flex
        flexDirection={"column"}
        // justifyContent={"center"}
      >
        <SortComponent />
      </Flex>
    </>
  );
};

export default FilterComponent;

const SortComponent = () => {
  return (
    <>
      <FormControl p=".5rem">
        <Box
          display="flex"
          alignItems="center"
          rounded={"md"}
          border="1px solid lightgrey"
          p=".75rem"
        >
          <Text>Sort By :</Text>
          <select border="none" outline="none">
            <option border="none" bg="transparent" outline="none">
              <Text>High to Low</Text>
            </option>
            <option border="none" bg="transparent" outline="none">
              <Text></Text>
            </option>
            <option border="none" bg="transparent" outline="none">
              Rating
            </option>
          </select>
        </Box>
      </FormControl>
    </>
  );
};
