import React from "react";
import { Box, Heading, Image, Text } from "@chakra-ui/react";
import Data from "../Data/data.json";
import { faker } from "@faker-js/faker";
// import uuid from "react-uuid";

function TrendingProduct(props) {
  // eslint-disable-next-line

  return (
    <>
      <ProdContainer {...props}>
        <ProdContainerHeader />
        <ProdContainerItem />
      </ProdContainer>
    </>
  );
}
const ProdContainerHeader = () => {
  return (
    <>
      <Box
        // px="1.5rem"
        py="1.5%"
        mx={{ base: "10%" }}
        display="flex"
        justifyContent={["center", "flex-start", "flex-start", "flex-start"]}
      >
        <Heading
          fontSize={["1.5rem", "1.75rem"]}
          fontWeight={["500", "600"]}
          as="h2"
          color="#2c2c2c"
        >
          <Box display={["inline-block"]}>Hot Products</Box>
          <Box
            borderBottom="2px solid #e97730"
            py={["1"]}
            pos={["relative"]}
            w={["70%", "60%"]}
            left={["4", "4"]}
            right={["4", "4"]}
          ></Box>
        </Heading>
      </Box>
    </>
  );
};

const ProdContainerItem = () => {
  let ShowcaseProduct = [];
  // console.log(productsArray);
  if (Data.length > 0) {
    for (let index = 0; index < 8; index++) {
      ShowcaseProduct = [...ShowcaseProduct, Data[index]];
    }
  }

  return (
    <>
      <Box
        mx={{ xl: "10%", lg: "10%", md: "10%" }}
        display={{ xl: "grid", lg: "grid" }}
        gridTemplateColumns={{ xl: "1fr 1fr 1fr 1fr", lg: "1fr 1fr 1fr 1fr" }}
      >
        <SingleProduct prod={ShowcaseProduct} />
      </Box>
    </>
  );
};

const SingleProduct = ({ prod }) => {
  // console.log(prod.id);
  const ImageSingleProduct = {
    img: faker.image.food(),
  };

  return (
    <>
      <Box border="1px solid red">
        {prod.map((item) => {
          return (
            <>
              <Box>
                <Image src={ImageSingleProduct.img} alt={item.name} />
              </Box>
              <Box>
                <Text>Add to Cart</Text>
              </Box>
            </>
          );
        })}
      </Box>
    </>
  );
};

const ProdContainer = ({ children, ...props }) => {
  return <Box w="100vw">{children}</Box>;
};

export default TrendingProduct;
